INSERT INTO `Associate` VALUES ('18','Silt Elemental','Summoned Creature','Dungeon Magazine 184','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSilt Elemental\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=18\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Silt Elemental<br/><span class=\"type\">Medium elemental magical beast (air, earth)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can expend a healing surge for the silt elemental if an effect allows it to spend one.<br/><b>Defenses</b> your defenses +2, not including any temporary bonuses or penalties.<br/><b>Speed</b>  7 </p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 1d10 + 5 + your level damage, plus an additional 2d6 damage if the target is slowed or immobilized.Level 15: 3d6 damage.Level 25: 5d6 damage.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 1d10 + 5 + your level damage, and the target is slowed until the end of your next turn.</p><p class=\"flavor alt\"> <b>