INSERT INTO `Associate` VALUES ('62','Flame Zephyr','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFlame Zephyr\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=62\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Flame Zephyr<br/><span class=\"type\">Small elemental magical beast (air, fire)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the zephyr if an effect allows it to spend one <br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 , fly 6 </p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Dust and Smoke</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 1</p><p class=\"flavorIndent\">Allies in the aura have partial concealment. You gain a +2 power bonus to damage rolls against enemies in the aura. At 11th level, this bonus increases to +3. It increases to +4 at 21st level.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z1a.gif\" /> <b>Standard Action</b> (fire) <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Close burst 1 (creatures in the burst); your level + 5 vs. Reflex</p><p class=\"flavorIndent\"><i>Hit:</i> 1d6 + your highest ability modifier fire damage, and you can slide the target 1 square.&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 2d6 + your highest ability modifier fire damage.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> (1/round)</p><p class=\"flavorIndent\"><i>Effect:</i> The zephyr crawls, runs, stands up, shifts, squeezes, or walks.</p><p class=\"flavor alt\"> <b>Resist</b> <b></b></p><p class=\"flavorIndent\">Resist: fire 10</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 52.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('63','Unicorn Destrier','Mount','Dragon Magazine 406','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nUnicorn Destrier\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=63\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Unicorn Destrier<br/><span class=\"type\">Large fey magical beast (mount)</span><br/></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the destrier if an effect allows it to spend one<br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 8 </p><p class=\"flavor alt\"> <b>Piercing Charger</b> (mount) <b></b></p><p class=\"flavorIndent\">If the destrier’s rider makes a charge attack and hits, the target of the attack is pushed 1 square and knocked prone.</p><p class=\"flavor alt\"><img src=\"images/symbol/S2.gif\" /> <b>Kick</b> (standard, at-will)</p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d6 + your Strength or Charisma modifier damage.</p><p class=\"flavor alt\"> <b>Fey Step</b> (move, encounter) <img src=\"images/symbol/x.gif\" /> <b>Teleportation</b></p><p class=\"flavorIndent\"><i>Effect:</i> The destrier and its rider teleport up to 5 squares. The destrier’s rider must teleport to a square adjacent to the destrier.</p><p class=\"flavor alt\"> <b>Horn Touch</b> (minor, encounter) </p><p class=\"flavorIndent\"><i>Effect:</i> One creature adjacent to the destrier can make a saving throw.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/406\" target=\"_new\">Dragon Magazine 406</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
