INSERT INTO `Associate` VALUES ('66','Air Archon Grunt','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nAir Archon Grunt\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=66\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Air Archon Grunt<br/><span class=\"type\">Medium elemental humanoid (air)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the archon if an effect allows it to spend one<br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 , fly 6 </p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Gift of Wind</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 2</p><p class=\"flavorIndent\">You gain a +2 power bonus to Acrobatics checks while you are in the aura.</p><p class=\"flavor alt\"> <b>Fluttering Form</b> <b></b></p><p class=\"flavorIndent\">Whenever the archon takes damage from a melee attack, the attacker can slide the archon 1 square.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d8 + your Charisma modifier damage, and the target is immobilized until the end of your next turn.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The archon walks, shifts, runs, stands up, squeezes, or crawls.</p><p class=\"flavor alt\"> <b>Opportunity Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy hits you with a melee attack while you are adjacent to the archon.</p><p class=\"flavorIndent\"><i>Effect:</i> The archon pushes the triggering enemy up to 3 squares.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 99.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('67','Earth Archon Grunt','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nEarth Archon Grunt\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=67\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Earth Archon Grunt<br/><span class=\"type\">Medium elemental humanoid (earth)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the archon if an effect allows it to spend one<br/><b>Defenses</b> your defenses (+2 to AC and Fortitude), not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 (earth walk)</p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Gift of Stone</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 2</p><p class=\"flavorIndent\">You gain a +2 power bonus to Endurance checks while you are in the aura.</p><p class=\"flavor alt\"> <b>Shattering Tremors</b> <b></b></p><p class=\"flavorIndent\">Whenever the archon takes thunder damage, it is slowed until the end of its next turn.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d10 + your Charisma modifier damage, and the archon pushes the target up to a number of squares equal to your Constitution modifier.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The archon walks, shifts, runs, stands up, squeezes, or crawls.</p><p class=\"flavor alt\"> <b>Opportunity Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy hits you with a melee attack while you are adjacent to the archon.</p><p class=\"flavorIndent\"><i>Effect:</i> The triggering enemy falls prone.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 99.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('68','Fire Archon Grunt','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFire Archon Grunt\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=68\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Fire Archon Grunt<br/><span class=\"type\">Medium elemental humanoid (fire)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the archon if an effect allows it to spend one<br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties; Resist 10 fire<br/><b>Speed</b> land 7 </p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Gift of Fire</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 2</p><p class=\"flavorIndent\">You gain a +2 power bonus to Intimidate checks while you are in the aura.</p><p class=\"flavor alt\"> <b>Guttering Flames</b> <b></b></p><p class=\"flavorIndent\">Whenever the archon takes cold damage, it cannot shift until the end of its next turn.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> (fire) <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 7 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d8 + your Charisma modifier fire damage, and the target gains vulnerable 5 fire until the end of your next turn.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The archon walks, shifts up to 3 squares, runs, stands up, squeezes, or crawls.</p><p class=\"flavor alt\"> <b>Opportunity Action</b> (fire) <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy hits you with a melee attack while it is within 7 squares of the archon.</p><p class=\"flavorIndent\"><i>Effect:</i> The archon shifts up to 7 squares to a square adjacent to the triggering enemy. The enemy then takes 5 fire damage.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 99.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
