INSERT INTO `Associate` VALUES ('74','Invisible Stalker','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nInvisible Stalker\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=74\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Invisible Stalker<br/><span class=\"type\">Large elemental humanoid (air)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can expend a healing surge for the invisible stalker if an effect allows it to spend one<br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 , fly 6 (hover)</p><p class=\"flavor alt\"> <b>Keen Senses</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b></p><p class=\"flavorIndent\">You gain a +5 power bonus to Perception checks while in the aura.</p><p class=\"flavor alt\"> <b>Natural Invisibility</b> <b></b></p><p class=\"flavorIndent\">The invisible stalker is invisible to creatures more than 1 square away from it.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Requirement:</i> The invisible stalker must not be grabbing a creature.</p><p class=\"flavorIndent\"><i>Attack:</i> Melee 2 (one creature); your level + 5 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d10 + your Intelligence modifier damage, and the target is grabbed (escape DC 24). Until the grab ends, the target takes ongoing 5 damage and grants combat advantage.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The invisible stalker either walks, shifts, runs, stands up, squeezes, crawls, or flies.</p><p class=\"flavor alt\"> <b>Opportunity Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An adjacent enemy misses the invisible stalker with an attack.</p><p class=\"flavorIndent\"><i>Effect:</i> The invisible stalker can shift 1 square.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 115.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('75','Air Monolith','Summoned Creature','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nAir Monolith\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=75\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Air Monolith<br/><span class=\"type\">Large elemental magical beast (air)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can expend a healing surge for the monolith if an effect allows it to spend one<br/><b>Defenses</b> your defenses + 2, not including any temporary bonuses or penalties; Vulnerable 5 fire<br/><b>Speed</b> land 0 , fly 8 (hover)</p><p class=\"flavor alt\"> <b>Phantom on the Wind</b> <b></b></p><p class=\"flavorIndent\">The monolith becomes invisible whenever it starts your turn without an enemy adjacent to it. The invisibility lasts until the end of your next turn or until it hits or misses with an attack.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 2 (one creature); your level + 5 vs. Reflex</p><p class=\"flavorIndent\"><i>Hit:</i> 2d8 + your Intelligence modifier damage, and the monolith can slide the target 1 square.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature that can’t see the monolith); your level + 5 vs. Reflex</p><p class=\"flavorIndent\"><i>Hit:</i> 4d8 + your Intelligence modifier damage, and the monolith grabs the target (escape DC 29) if it does not have a creature grabbed. Until the grab ends, the target takes ongoing 10 damage, and any ranged or melee attacks that hit the monolith deal only half damage to it and the other half to the grabbed target.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The monolith either walks, shifts, runs, stands up, squeezes, or crawls.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 129.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
