INSERT INTO `Associate` VALUES ('79','Arctine','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nArctine\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=79\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Arctine<br/><span class=\"type\">Small elemental humanoid (cold)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 (ice walk)</p><p class=\"flavor alt\"> <b>Passive Benefit: Frost Armor</b> <b></b></p><p class=\"flavorIndent\">You have ice walk while you shift, and you have resist 3 cold (6 at 11th level and 9 at 21st level).</p><p class=\"flavor alt\"> <b>Active Benefit: Minor Action</b> (zone) <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Effect:</i> The arctine creates a zone of difficult terrain in a close burst 1. Creatures with ice walk are unaffected. The zone lasts until the end of the encounter or until the arctine leaves the zone, dies, or enters passive mode.</p><p class=\"flavor alt\"><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 147.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascrip