INSERT INTO `Associate` VALUES ('80','Automaton','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nAutomaton\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=80\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Automaton<br/><span class=\"type\">Small elemental humanoid (construct)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 5 </p><p class=\"flavor alt\"> <b>Passive Benefit: Self-Repair</b> <b></b></p><p class=\"flavorIndent\">You gain a +2 power bonus to saving throws against ongoing damage.</p><p class=\"flavor alt\"> <b>Active Benefit: Fearsome Presence</b> <b></b></p><p class=\"flavorIndent\">You and your allies can flank with the automaton.</p><p class=\"flavor alt\"> <b>Active Benefit: Immediate Interrupt</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> The automaton takes damage from an attack while you are within 2 squares of it.</p><p class=\"flavorIndent\"><i>Effect:</i> The automaton takes no damage from the attack. You take the damage instead.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 148.</p>\r\n    </di