INSERT INTO `Associate` VALUES ('81','Chaos Phage','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nChaos Phage\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=81\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Chaos Phage<br/><span class=\"type\">Small elemental magical beast</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 </p><p class=\"flavor alt\"> <b>Passive Benefit: Mad Delusions</b> (psychic) <b></b></p><p class=\"flavorIndent\">You gain a +1 power bonus to Will. When the chaos phage enters active mode, you take psychic damage equal to 1 + one-half your level.</p><p class=\"flavor alt\"> <b>Active Benefit: Immediate Reaction</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy adjacent to the chaos phage becomes bloodied.</p><p class=\"flavorIndent\"><i>Effect:</i> The chaos phage enters passive mode, and the triggering enemy grants combat advantage (save ends). Until this effect ends, the chaos phage cannot enter active mode.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 148.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('82','Satyr of the Night','Summoned Creature','Dragon Magazine 406','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSatyr of the Night\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=82\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Satyr of the Night<br/><span class=\"type\">Medium fey humanoid</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can expend a healing surge for the satyr if an effect allows it to spend one.<br/><b>Defenses</b> your defenses + 2, not including any temporary bonuses or penalties.<br/><b>Speed</b> land 6 </p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 6 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 1d8 + your Charisma modifier damage, and the satyr can slide the target 1 square. The target is slowed until the end of your next turn.</p><p class=\"flavor alt\"> <b>Minor Action</b> (teleportation) <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The satyr can teleport up to 3 squares.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The satyr either walks, shifts, runs, stands up, squeezes, or crawls.</p><p class=\"flavor alt\"> <b>Opportunity Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy leaves a square adjacent to the satyr without shifting, or makes a ranged attack while adjacent to the satyr.</p><p class=\"flavorIndent\"><i>Effect:</i> The satyr uses its standard action attack against the triggering enemy.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/406\" target=\"_new\">Dragon Magazine 406</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('83','Displacer Beast','Summoned Creature','Dragon Magazine 406','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDisplacer Beast\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=83\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Displacer Beast<br/><span class=\"type\">Large fey magical beast</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can expend a healing surge for the displacer beast if an effect allows it to spend one.<br/><b>Defenses</b> your defenses + 2, not including any temporary bonuses or penalties.<br/><b>Speed</b> land 10 </p><p class=\"flavor alt\"> <b>Displacement</b> (illusion) <b></b></p><p class=\"flavorIndent\">When a melee or a ranged attack hits the displacer beast, roll a d20. On a 10 or higher, the attack misses instead. On a 9 or lower, the displacer beast loses this trait until the start of your next turn.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Standard Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 2 (one creature); your level + 5 vs. Reflex</p><p class=\"flavorIndent\"><i>Hit:</i> 3d8 + your Charisma modifier damage, and the displacer beast slides the target up to 3 squares.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The displacer beast shifts up to half its speed.</p><p class=\"flavor alt\"> <b>Minor Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b> 1/round</p><p class=\"flavorIndent\"><i>Effect:</i> The displacer beast either walks, runs, stands up, squeezes, or crawls.</p><p class=\"flavor alt\"> <b>Immediate Interrupt</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy within 2 squares of the displacer beast makes an attack against you.</p><p class=\"flavorIndent\"><i>Effect:</i> The displacer beast uses its standard action attack against the triggering enemy.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/406\" target=\"_new\">Dragon Magazine 406</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('84','Crysmalite','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCrysmalite\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=84\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Crysmalite<br/><span class=\"type\">Small elemental magical beast (spider)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 , climb 3 (spider climb)</p><p class=\"flavor alt\"> <b>Passive Benefit: Uncanny Senses and Grip</b> <b></b></p><p class=\"flavorIndent\">You gain a +2 power bonus to Perception checks, and you gain a +2 bonus to Athletics checks to climb.</p><p class=\"flavor alt\"> <b>Active Benefit: Free Action</b> <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Trigger:</i> You make a blast or burst attack, and the crysmalite is in the area of effect.</p><p class=\"flavorIndent\"><i>Effect:</i> Enemies adjacent to the crysmalite take a -2 penalty to attack rolls until the end of your next turn.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 148.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('85','Flame Serpent','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFlame Serpent\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=85\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Flame Serpent<br/><span class=\"type\">Small elemental magical beast (fire)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 6 </p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Passive Benefit: Burning Inside</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 2</p><p class=\"flavorIndent\">The aura is brightly lit. You can activate or deactivate this aura as a minor action.</p><p class=\"flavor alt\"> <b>Passive Benefit: Fire Within</b> <b></b></p><p class=\"flavorIndent\">You have resist 3 fire (6 at 11th level and 9 at 21st level).</p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Active Benefit: Flickering Flames</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 1</p><p class=\"flavorIndent\">Any enemy in the aura grants combat advantage to you when you use a fire power against it.</p><p class=\"flavor alt\"> <b>Active Benefit: Move Action</b> <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Effect:</i> The flame serpent shifts up to its speed.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 148.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('86','Hordeling','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nHordeling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=86\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Hordeling<br/><span class=\"type\">Small elemental humanoid (demon)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 4 </p><p class=\"flavor alt\"> <b>Passive Benefit: Vicious</b> <b></b></p><p class=\"flavorIndent\">You gain a +2 power bonus to opportunity attack rolls.</p><p class=\"flavor alt\"> <b>Passive Benefit: Rattle the Cage</b> <b></b></p><p class=\"flavorIndent\">Whenever you end the encounter with the hordeling in passive mode, you take damage equal to your level.</p><p class=\"flavor alt\"> <b>Active Benefit: Standard Action</b> (fear) <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Effect:</i> The hordeling can push each creature adjacent to it 1 square.</p><p class=\"flavor alt\"> <b>Active Benefits: Immediate Interrupt</b> <img src=\"images/symbol/x.gif\" /> <b>Encounter</b></p><p class=\"flavorIndent\"><i>Trigger:</i> You or the hordeling takes acid, cold, fire, lightning, or thunder damage.</p><p class=\"flavorIndent\"><i>Effect:</i> You and the hordeling each gain resist 5 to the triggering damage type, or types, until the end of your next turn (10 at 11th level and 15 at 21st level).</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 149.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('87','Mud Wretch','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMud Wretch\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=87\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Mud Wretch<br/><span class=\"type\">Small elemental magical beast (earth, water)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 4 </p><p class=\"flavor alt\"> <b>Passive Benefit: Corrosive Secretions</b> <b></b></p><p class=\"flavorIndent\">Any enemy that grabs you immediately takes 3 acid damage (6 at 11th level and 9 at 21st level).</p><p class=\"flavor alt\"> <b>Passive Benefit: Unnatural Flexibility</b> <b></b></p><p class=\"flavorIndent\">Squeezing doesn’t cause you to move at half speed.</p><p class=\"flavor alt\"><img src=\"images/symbol/aura.png\" align=\"top\" /> <b>Active Benefit: Noxious Vapors</b> <img src=\"images/symbol/x.gif\" /> <b>Aura</b> 1</p><p class=\"flavorIndent\">While you are bloodied, enemies in the aura take a -1 penalty to attack rolls.</p><p class=\"flavor alt\"> <b>Active Benefits: Move Action</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Effect:</i> The mud wretch shifts up to 2 squares.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 149.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Associate` VALUES ('88','Magmin','Elemental Companion','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMagmin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=88\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Magmin<br/><span class=\"type\">Medium elemental humanoid (earth, fire)</span><br/><span class=\"level\">Elemental Companion</span></h1><p class=\"flavor\"><b>HP</b> 1; a missed attack never damages the companion<b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 4 </p><p class=\"flavor alt\"> <b>Passive Benefit: Fiery Intensity</b> <b></b></p><p class=\"flavorIndent\">You gain a +2 power bonus to Intimidate checks.</p><p class=\"flavor alt\"> <b>Passive Benefit: Flowing Lava</b> <b></b></p><p class=\"flavorIndent\">When you take fire damage, you gain a +2 power bonus to speed until the end of your next turn. When you take cold damage, you are slowed until the end of your next turn.</p><p class=\"flavor alt\"> <b>Active Benefit: Immediate Reaction</b> <b></b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy adjacent to the magmin becomes bloodied.</p><p class=\"flavorIndent\"><i>Effect:</i> The triggering enemy is weakened until the end of your next turn.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 149.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
