INSERT INTO `Associate` VALUES ('94','Duodrone Balancer','Summoned Creature','Dragon Magazine 414','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDuodrone Balancer\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=associate&amp;id=94\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"monster\">Duodrone Balancer<br/><span class=\"type\">Medium immortal animate (modron)</span><br/><span class=\"level\">Summoned Creature</span></h1><p class=\"flavor\"><b>HP</b> your bloodied value; <b>Healing Surges</b> none, but you can spend a healing surge for the balancer if an effect allows it to spend one<br/><b>Defenses</b> your defenses, not including any temporary bonuses or penalties<br/><b>Speed</b> land 5 , fly 5 (clumsy)</p><p class=\"flavor alt\"> <b>Implacable</b> <b></b></p><p class=\"flavorIndent\">An enemy cannot enter the balancer’s space by any means.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Remove Outlier</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Attack:</i> Melee 1 (one creature); your level + 5 vs. AC</p><p class=\"flavorIndent\"><i>Hit:</i> 2d6 + your level damage, and the target cannot score critical hits until the end of your next turn.</p><p class=\"flavor alt\"> <b>Probabilistic Understanding</b> <img src=\"images/symbol/x.gif\" /> <b>Recharge</b> when an enemy within 10 squares of the balancer scores a critical hit</p><p class=\"flavorIndent\"><i>Trigger:</i> The balancer’s summoner makes a d20 roll.</p><p class=\"flavorIndent\"><i>Effect (Free Action):</i> The summoner treats the triggering roll as though he or she rolled a 10.</p><p class=\"flavor alt\"><img src=\"images/symbol/Z2a.gif\" /> <b>Impose Order</b> <img src=\"images/symbol/x.gif\" /> <b>At-Will</b></p><p class=\"flavorIndent\"><i>Trigger:</i> An enemy willingly leaves a square adjacent to the balancer without shifting or teleporting.</p><p class=\"flavorIndent\"><i>Effect (Opportunity Action):</i> The balancer uses remove outlier against the triggering enemy.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/414\" target=\"_new\">Dragon Magazine 414</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
