INSERT INTO `Companion` VALUES ('1','Bear','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBear\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=1\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Bear</h1><p class=\"flavortext\">Brown bears and similar creatures fall into this category. Bears are tougher and deadlier than other beast companions, but they are a little slower.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 16, Constitution 14, Dexterity 12, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>5 squares<br/><b>Defenses: </b>AC 12 + level, Fortitude 14 + level, Reflex 10 + level, Will 12 + level<br/><b>Hit Points: </b>16 + 10 per level<br/><b>Attack Bonus: </b>Level + 2<br/><b>Damage: </b>1d12<br/><b>Melee Basic Attack: </b>Claw; level + 2 vs. AC; 1d12 + Strength modifier damage.<br/><b>Trained Skills: </b>Athletics, Endurance<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 39.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('2','Boar','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBoar\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=2\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Boar</h1><p class=\"flavortext\">Boars are tough, resilient, and ferocious companions.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 16, Constitution 16, Dexterity 14, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>6 squares, 8 when charging<br/><b>Defenses: </b>AC 14 + level, Fortitude 12 + level, Reflex 10 + level, Will 12 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Gore; level + 4 vs. AC; 1d8 + Strength modifier damage.<br/><b>Charge: </b>When charging, a boar gains a +2 bonus to its damage roll.<br/><b>Trained Skill: </b>Endurance<br/><b>Charge: </b>When charging, a boar gains a +2 bonus to its damage roll.<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 39.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('3','Cat','Companion','Martial Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCat\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=3\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Cat</h1><p class=\"flavortext\">Cats include lions, tigers, cougars, and similar predators. Their natural agility and stealth allow them to excel on scouting missions.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 14, Constitution 12, Dexterity 16, Intelligence 6, Wisdom 14, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>7 squares<br/><b>Defenses: </b>AC 14 + level, Fortitude 11 + level, Reflex 13 + level, Will 12 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Claw; level +  vs. ; 1d8 + Dexterity modifier damage.<br/><b>Trained Skills: </b>Athletics, Stealth<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 40, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 131.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('4','Lizard','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLizard\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=4\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Lizard</h1><p class=\"flavortext\">The lizard category includes crocodiles and drakes. These thick-skinned beasts are tough combatants.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 16, Constitution 14, Dexterity 16, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>6 squares<br/><b>Defenses: </b>AC 15 + level, Fortitude 12 + level, Reflex 12 + level, Will 10 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Bite or Claw; level +  vs. ; 1d8 + Strength modifier damage.<br/><b>Opportunity Attack: </b>A lizard gains a +2 bonus to the attack roll when making an opportunity attack.<br/><b>Trained Skills: </b>Athletics, Endurance<br/><b>Opportunity Attack: </b>A lizard gains a +2 bonus to the attack roll when making an opportunity attack.<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 40.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getEleme