INSERT INTO `Companion` VALUES ('5','Raptor','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRaptor\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=5\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Raptor</h1><p class=\"flavortext\">The raptor category includes eagles, falcons, hawks, and other birds of prey. They are mobile and agile but not as ferocious as other beast companions.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 12, Constitution 12, Dexterity 18, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Small<br/><b>Speed: </b>2 squares, Fly 7 squares (hover)<br/><b>Defenses: </b>AC 14 + level, Fortitude 10 + level, Reflex 14 + level, Will 12 + level<br/><b>Hit Points: </b>12 + 6 per level<br/><b>Attack Bonus: </b>Level + 5<br/><b>Damage: </b>1d6<br/><b>Melee Basic Attack: </b>Claw; level +  vs. ; 1d6 + Dexterity modifier damage.<br/><b>Trained Skill: </b>Perception<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 40.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c ==