INSERT INTO `Companion` VALUES ('7','Spider','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSpider\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=7\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Spider</h1><p class=\"flavortext\">In the Underdark and in primeval forests, goblins and many fey creatures train hunting spiders.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 14, Constitution 12, Dexterity 16, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>6 squares, climb 6 squares (spider climb)<br/><b>Defenses: </b>AC 14 + level, Fortitude 11 + level, Reflex 12 + level, Will 12 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Bite; level +  vs. ; 1d8 + Dexterity modifier damage.<br/><b>Trained Skills: </b>Athletics, Stealth<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 41.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('8','Wolf','Companion','Martial Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWolf\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=8\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Wolf</h1><p class=\"flavortext\">The wolf category includes predatory canines and doglike creatures, from mastiffs to timber wolves to jackals.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 14, Constitution 14, Dexterity 14, Intelligence 6, Wisdom 14, Charisma 6<br/><b>Size: </b>Medium<br/><b>Speed: </b>7 squares<br/><b>Defenses: </b>AC 14 + level, Fortitude 12 + level, Reflex 12 + level, Will 13 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Bite; level +  vs. ; 1d8 + Strength modifier damage.<br/><b>Combat Advantage: </b>When a wolf has combat advantage against a target, the wolf gains a bonus to damage rolls against the target equal to the wolf’s Wisdom modifier.<br/><b>Trained Skills: </b>Endurance, Perception<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217897200\" target=\"_new\">Martial Power</a>, page(s) 41.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('9','Bat','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBat\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=9\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Bat</h1><p class=\"flavor\"><i>Bats are common familiars for warlocks and those who prefer subtle magic and stealth.</i></p><span class=\"clearIndent\"><b>Senses </b>blindsight 5</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks and Stealth checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">See the Unseen: As a minor action, you cause enemies adjacent to your bat familiar to lose the benefits of concealment (but not total concealment) against you until the end of your current turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 131.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('10','Book Imp','Familiar','Arcane Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBook Imp\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=10\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Book Imp</h1><p class=\"flavor\"><i>Book imps are masters of knowledge, dispatched by the powers of Hell to offer advice to and tempt mortal spellcasters.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana checks and History checks.<br/>* You can read and speak Supernal.<br/>* You gain resist fire 5. If you already have resist fire, increase your resistance by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Deceptive Move: As a minor action, you cause your book imp to become invisible until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('11','Owl','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nOwl\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=11\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Owl</h1><p class=\"flavor\"><i>A common symbol of wisdom and knowledge, owl familiars are known for their good counsel and insight.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Whenever you make an Insight check or a Perception check, add 1d4 to the result.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Owl’s Eyes: As a standard action, you can see through your familiar’s eyes until the end of your next turn. You do not gain the ability to use powers or attack through the familiar, and you determine if a target has cover and concealment from you as normal, but you might spot an enemy coming around a corner or notice a foe hiding behind a wall.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('12','Bound Demon','Familiar','Arcane Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBound Demon\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=12\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Bound Demon</h1><p class=\"flavor\"><i>This ugly blob of flesh is a creature bound to your service, but it would prefer to be back in the Abyss.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 3 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks.<br/>* You can read and speak Abyssal.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Alchemical Synergy: Once per encounter, when you hit a bloodied creature adjacent to your bound demon, you gain 1d8 temporary hit points.<br/>Level 11: 2d8 temporary hit points.<br/>Level 21: 3d8 temporary hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('14','Crafter Homunculus','Familiar','Arcane Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCrafter Homunculus\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=14\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Crafter Homunculus</h1><p class=\"flavor\"><i>This humanoid figure has indistinct features and looks like it’s made of clay. Arcane crafters use homunculi as skilled assistants.</i></p><span class=\"clearIndent\"><b>Speed </b>5</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana checks.<br/>* You gain a +2 bonus to skill checks related to rituals of the creation category.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Blood Feast: An enemy adjacent to your crafter homunculus takes a -1 penalty to all defenses against alchemical items. Your crafter homunculus is immune to your alchemical items.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('15','Dragonling','Familiar','Arcane Power','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDragonling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=15\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Dragonling</h1><p class=\"flavor\"><i>Sorcerers who follow the draconic tradition, along with other spellcasters who rely on endurance and physical might, manifest dragonling familiars.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can read and speak Draconic. When you spend a healing surge, you regain 2 additional hit points.<br/>Level 11: 3 additional hit points.<br/>Level 21: 4 additional hit points.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Dragon’s Breath: Once per encounter, you can use your dragonling’s space as the origin square for a close blast arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('16','Falcon','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFalcon\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=16\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Falcon</h1><p class=\"flavor\"><i>Warrior spellcasters, those that love to wade into the fray, see the fighting falcon as the ideal familiar.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You gain a +2 bonus to all defenses against opportunity attacks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Hunting Falcon: Once per encounter, you can determine range for your ranged arcane attacks from your falcon until the end of your current turn.<br/>Soaring Spirit: A falcon familiar has a range limit of 40 squares from you.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('18','Rat','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRat\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=18\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Rat</h1><p class=\"flavor\"><i>The rat is a scavenger capable of infiltrating even the most tightly guarded places.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Rat’s Cunning: A rat familiar can carry an object that weighs 2 pounds, and it can manipulate objects to make Thievery checks. It gains a +2 bonus to such checks. A rat gains a +5 bonus to its Stealth checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 141, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('19','Raven','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRaven\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=19\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Raven</h1><p class=\"flavor\"><i>Ravens are known for their keen senses and skill in uncovering hidden knowledge.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +4 bonus to Perception checks made to find hidden objects.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Raven’s Speech: A raven can speak any language you know, and it can converse with other creatures. When it speaks, you hear everything it says and hear everything spoken to it. You can dictate its answers if you so choose.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 141, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('20','Cat','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCat\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=20\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Cat</h1><p class=\"flavor\"><i>Cats are willful, independent familiars, and their willingness to act alone makes them excellent spies.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\"><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You gain a +2 bonus to Acrobatics checks.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You can make an Acrobatics check to reduce the damage you take from falling even if you are not trained in that skill.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Independent Spirit: A cat familiar has no range limit on the distance it can exist away from you.<br/>Silent Predator: A cat familiar gains a +5 bonus to its Stealth checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 140, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 131.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('21','Serpent','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSerpent\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=21\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Serpent</h1><p class=\"flavor\"><i>The serpent is a deceptive creature, and it offers its master the same mastery of guile and cunning.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\"><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You gain a +2 bonus to Bluff checks.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; When you shift, you can ignore the difficult terrain in a single square of that movement.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Serpent’s Startle: Once per encounter as a minor action, all creatures adjacent to your serpent familiar can’t make opportunity attacks until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 141, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('22','Spider','Familiar','Arcane Power, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSpider\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=22\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Spider</h1><p class=\"flavor\"><i>Spiders are guardians of hidden knowledge that weave webs capable of grabbing and moving small objects.</i></p><span class=\"clearIndent\"><b>Speed </b>4, climb 4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks to climb.<br/>* Enemies take a -1 penalty to saving throws against your arcane powers that immobilize or slow them.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Spider’s Web: As a minor action, a spider can slide an object within 5 squares of it 2 squares. The object must be unattended, and it can weigh no more than 5 pounds.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949571\" target=\"_new\">Arcane Power</a>, page(s) 141, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('23','Arcane Eye','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nArcane Eye\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=23\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Arcane Eye</h1><p class=\"flavor\"><i>This hovering eyeball enhances your vision and sees in places you can’t.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You gain a +2 bonus to skill checks for Scrying rituals.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">See All: Once per encounter, you can determine range, line of sight, and line of effect for one ranged arcane implement attack from the eye, and you ignore concealment (but not total concealment) for that attack.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('24','Badger','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBadger\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=24\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Badger</h1><p class=\"flavor\"><i>Casters who want a stronger defense manifest badgers to protect them, as do those who are dangerous when they are hurt.</i></p><span class=\"clearIndent\"><b>Speed </b>5, burrow 2</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks.<br/>* You gain a +1 bonus to damage rolls while bloodied.<br/>&nbsp;&nbsp;&nbsp;&nbsp;This bonus increases to +2 at 11th level and +3 at 21st level.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Devoted Minion: If your badger is adjacent to you when you are hit by an attack, the badger is reduced to 0 hit points, and you reduce the damage you take by your Constitution modifier.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('25','Canine Construct','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCanine Construct\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=25\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Canine Construct</h1><p class=\"flavor\"><i>This dog-shaped familiar is a loyal, valiant guardian.</i></p><span class=\"clearIndent\"><b>Speed </b>7</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks.<br/>* You gain a +2 bonus to skill checks for Warding rituals.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Tenacious Guard: You gain resist 1 against ranged and melee attacks while adjacent to the canine construct. This increases to resist 2 at 11th level and resist 3 at 21st level.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('26','Disembodied Hand','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDisembodied Hand\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=26\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Disembodied Hand</h1><p class=\"flavor\"><i>This hand crawls around using its digits, and it can expertly manipulate items.</i></p><span class=\"clearIndent\"><b>Speed </b>6, climb 2</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can retrieve or stow an item as a free action instead of as a minor action.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Agile Digits: A disembodied hand can open latches and turn knobs that require only one hand to operate. It can also manipulate objects to make Thievery checks. It gains a +2 bonus to such checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('27','Fire Lizard','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFire Lizard\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=27\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Fire Lizard</h1><p class=\"flavor\"><i>These blazing hot lizards are favored familiars for tieflings and pyromancers.</i></p><span class=\"clearIndent\"><b>Senses </b>Low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics and Endurance checks.<br/>* You gain resist 5 fire. If you already have resist fire, increase it by 2. Your resist fire increases by 2 while you are bloodied.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Lizard’s Immunity: The fire lizard is immune to fire.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('28','Lightning Lizard','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLightning Lizard\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=28\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Lightning Lizard</h1><p class=\"flavor\"><i>Storm sorcerers and other lightning-focused casters choose lightning lizards as familiars.</i></p><span class=\"clearIndent\"><b>Senses </b>Low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Acrobatics and Athletics checks.<br/>* You gain resist 5 lightning. If you already have resist lightning, increase it by 2. Your resist lightning increases by 2 while you are bloodied.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Lizard’s Immunity: The lightning lizard is immune to lightning.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('29','Ooze','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nOoze\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=29\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Ooze</h1><p class=\"flavor\"><i>This colorful, blobby familiar is a boon to those who explore underground areas.</i></p><span class=\"clearIndent\"><b>Senses </b>tremorsense 5</span><span class=\"clearIndent\"><b>Speed </b>5</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Dungeoneering checks.<br/>* You gain resist 5 acid. If you already have resist acid, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Ooze’s Malleability: The ooze can squeeze through gaps as small as half an inch wide.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('30','Parrot','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nParrot\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=30\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Parrot</h1><p class=\"flavor\"><i>Social arcane casters prefer talkative parrot familiars.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 5 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Diplomacy and Streetwise checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Parrot’s Speech: A parrot can speak any language you know, and it can converse with other creatures. When it speaks, you hear everything it says and hear everything spoken to it. You can dictate its answers if you so choose.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 26.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('31','Rootling','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRootling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=31\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Rootling</h1><p class=\"flavor\"><i>A bundle of roots and twigs, a rootling shows its master’s connection to the natural world.</i></p><span class=\"clearIndent\"><b>Speed </b>6, climb 4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Nature checks.<br/>* You gain forest walk (you ignore difficult terrain if it’s the result of trees, underbrush, plants, or natural growth).</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Rootling Camouflage: A rootling gains a +5 bonus to Stealth checks if it is in a forest or has concealment due to plants.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 26.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('32','Scout Homunculus','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nScout Homunculus\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=32\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Scout Homunculus</h1><p class=\"flavor\"><i>With tiny wings, this homunculus can scout in elevated places. It is also used by casters who often scry.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, fly 3 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth.<br/>* You gain a +2 bonus to skill checks for Scrying rituals.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Glamered Scout: A scout homunculus rolls two d20s and takes the higher result for Stealth checks.<br/>Independent Spirit: A scout homunculus can exist up to a mile away from you.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 26.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('33','Skull','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSkull\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=33\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Skull</h1><p class=\"flavor\"><i>An arcane character with a dark side might choose a skull as a familiar, drawing on its accumulated magical knowledge.</i></p><span class=\"clearIndent\"><b>Speed </b>fly 4 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana and Intimidate checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Skull’s Gaze: Once per encounter, you can gain a +2 bonus to an attack roll against a creature adjacent to the skull if you are using an arcane power with the fear keyword.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('34','Toad','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nToad\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=34\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Toad</h1><p class=\"flavor\"><i>Toughness and durability are tough to come by for most arcane practitioners, but a toad familiar can close the gap.</i></p><span class=\"clearIndent\"><b>Speed </b>3</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Endurance checks.<br/>* You gain a +2 bonus to your healing surge value.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Toad’s Healing: Once per encounter, when an ally adjacent to the toad spends a healing surge, that ally regains 1d8 extra hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('35','Weasel','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWeasel\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=35\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Weasel</h1><p class=\"flavor\"><i>Masters of weasel familiars are regarded as both quick-witted and quick-footed.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Acrobatics and Bluff checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Silent Predator: A weasel gains a +5 bonus to Stealth checks.<br/>Weasel’s Sleekness: The weasel gains a +5 bonus to AC against opportunity attacks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('36','Air Mephit','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nAir Mephit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=36\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Air Mephit</h1><p class=\"flavor\"><i>A manifestation of air, this creature appears to be a miniature winged humanoid made entirely of swirling wind.</i></p><span class=\"clearIndent\"><b>Speed </b>4, fly 7 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff checks.<br/>* You can read and speak Primordial.<br/>* You gain resist 5 lightning and resist 5 thunder. If you already resist one of those damage types, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Form of Air: As a minor action, you can cause the air mephit to become invisible until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 28.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('37','Arcane Wisp','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nArcane Wisp\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=37\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Arcane Wisp</h1><p class=\"flavor\"><i>Fey pact warlocks and others who like to teleport choose these floating orbs of light as familiars.</i></p><span class=\"clearIndent\"><b>Speed </b>fly 8 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth checks.<br/>* When you teleport, add 1 to the distance you teleport.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Wisp’s Escape: The first time the arcane wisp is attacked in an encounter, it can teleport 5 squares as an immediate interrupt.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 28.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('38','Beholderkin','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBeholderkin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=38\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Beholderkin</h1><p class=\"flavor\"><i>A spherical creature with a large central eye and a few eyes on stalks, the beholderkin is the chosen familiar of chaotic mages.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>fly 8 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You can read and speak Deep Speech.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Beholderkin Eye Ray: Once per encounter, you can have the beholder shoot an eye ray as a minor action. The beholderkin deals 1d10 damage to a creature within 5 squares of it. Roll 1d4 to determine the damage type: 1—acid, 2—fire, 3—necrotic, 4—poison.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 28.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('39','Blackspawn Darkling','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBlackspawn Darkling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=39\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Blackspawn Darkling</h1><p class=\"flavor\"><i>This reptilian familiar has two curving horns and black scales.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>8, (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth checks.<br/>* You can read and speak Draconic.<br/>* You gain resist 5 acid. If you already have resist acid, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Blackspawn Stealth: The blackspawn darkling gains a +5 bonus to Stealth checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 28.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('40','Bluespawn Nimblespark','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBluespawn Nimblespark\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=40\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Bluespawn Nimblespark</h1><p class=\"flavor\"><i>A single prominent horn juts from this reptile’s snout, and its blue scales carry a static charge.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics and Nature checks.<br/>* You can read and speak Draconic.<br/>* You gain resist 5 lightning. If you already have resist lightning, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Bluespawn Shock: If the bluespawn is hit by an attack, the creature that hit it takes 2d6 lightning damage.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 28.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('41','Earth Mephit','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nEarth Mephit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=41\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Earth Mephit</h1><p class=\"flavor\"><i>A manifestation of air, this creature appears to be a miniature winged humanoid made entirely of swirling wind.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 4 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff and Endurance checks.<br/>* You can read and speak Primordial.<br/>* When you would be pushed, pulled, or slid, reduce the forced movement by 1 square.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Form of Earth: Once per encounter as a minor action, you can cause the earth mephit to enlarge into a stone statue. It gains resist 20 all and occupies the square until the end of your next turn. You can use this ability only if the mephit is in an unoccupied square.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('42','Fire Mephit','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFire Mephit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=42\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Fire Mephit</h1><p class=\"flavor\"><i>These tiny, winged, humanoids are manifested by spellcasters who like to play with fire.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff checks.<br/>* You can read and speak Primordial.<br/>* You gain resist 5 fire. If you already have resist fire, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Form of Fire: As a minor action, you can cause the fire mephit to glow or to stop glowing. While glowing, it sheds bright light in its square and in a 10-square radius.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('43','Grayspawn Shortfang','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGrayspawn Shortfang\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=43\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Grayspawn Shortfang</h1><p class=\"flavor\"><i>Spiky projections cover this familiar’s body, and its tail is tipped with two long spikes.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>7, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana and Intimidate checks.<br/>* You can read and speak Draconic.<br/>* You gain resist 5 acid. If you already have resist acid, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Grayspawn Toughness: A grayspawn gains a +2 bonus to all defenses.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('44','Greenspawn Banespeak','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGreenspawn Banespeak\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=44\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Greenspawn Banespeak</h1><p class=\"flavor\"><i>This green-scaled reptile’s head is crested with several long spines, and its body is lithe and agile.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>8</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff checks.<br/>* You can read and speak Draconic.<br/>* You gain resist 5 poison. If you already have resist poison, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Greenspawn’s Diversion: Once per encounter as a minor action, you can make a Bluff check against the passive Insight of an enemy adjacent to the greenspawn. If successful, you gain combat advantage against the target on your next attack made before the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('45','Ice Mephit','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nIce Mephit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=45\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Ice Mephit</h1><p class=\"flavor\"><i>This crystalline humanoid looks like it’s made of ice, and its tiny wings shed ice crystals as they flap.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 4 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff checks.<br/>* You can read and speak Primordial.<br/>* You gain resist 5 cold. If you already have resist cold, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Form of Ice: Once per encounter as a minor action, you can cause the ice mephit’s square and each adjacent square to become difficult terrain until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('46','Rakshasa Claw','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRakshasa Claw\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=46\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Rakshasa Claw</h1><p class=\"flavor\"><i>This gnarled, furred hand has long talons and is a favored familiar for those who want to pierce illusions.</i></p><span class=\"clearIndent\"><b>Speed </b>7, climb 3</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana checks.<br/>* You gain a +5 bonus to Insight checks to recognize effects as illusory.<br/>* You can retrieve or stow an item as a free action instead of as a minor action.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Agile Digits: A rakshasa claw can open latches and turn knobs that require only one hand to operate. It can also manipulate objects to make Thievery checks. It gains a +2 bonus to such checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 27.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('47','Redspawn Spitfire','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRedspawn Spitfire\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=47\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Redspawn Spitfire</h1><p class=\"flavor\"><i>This reptilian familiar is hot to the touch, and it has red scales.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, fly 5 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks.<br/>* You can read and speak Draconic.<br/>* You gain resist 5 fire. If you already have resist fire, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Redspawn Toughness: A redspawn gains a +2 bonus to all defenses.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 30.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('48','Stone Fowl','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nStone Fowl\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=48\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Stone Fowl</h1><p class=\"flavor\"><i>Similar in appearance to a cockatrice, this familiar helps you resist the effects of poisons or petrification.</i></p><span class=\"clearIndent\"><b>Speed </b>5, fly 2 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Dungeoneering checks.<br/>* You gain resist 5 poison. If you already have resist poison, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Cockatrice’s Defense: Once per encounter, when you make a saving throw against a slowed, immobilized, or petrified effect while adjacent to the stone fowl, you gain a +5 power bonus to the saving throw.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 30.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('49','Whitespawn Snowstepper','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWhitespawn Snowstepper\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=49\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Whitespawn Snowstepper</h1><p class=\"flavor\"><i>Ice crystals cake this reptile’s white scales, and a thin sheet of ice coats anything it perches upon.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>7 (ice walk), fly 3 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can read and speak Draconic.<br/>* You gain resist 5 cold. If you already have resist cold, increase it by 2.<br/>* You gain ice walk (you ignore difficult terrain caused by snow and ice).</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Whitespawn Minion: If the whitespawn is adjacent to you when you are hit by an attack, you can reduce the amount of damage you take by 5+ your Constitution modifier. If you do so, the whitespawn drops to 0 hit points. If the damage is cold damage, you reduce the damage by 10 + your Constitution modifier.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 30.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('50','Blazing Skull','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBlazing Skull\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=50\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Blazing Skull</h1><p class=\"flavor\"><i>This skull is wreathed in fire and holds knowledge about many things—mostly flammable things.</i></p><span class=\"clearIndent\"><b>Speed </b>fly 10 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana and Stealth checks.<br/>* You gain resist 5 fire. If you already have resist fire, increase it by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Skull’s Gaze: Once per encounter, you can gain a +2 bonus to an attack roll against a creature adjacent to the skull if you are using an arcane power with the fear or fire keyword.<br/>Fire Light: As a minor action, you can cause the blazing skull to glow or to stop glowing. While glowing, it sheds bright light in its square and in a 20-square radius.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 30.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('51','Chaos Shard','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nChaos Shard\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=51\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Chaos Shard</h1><p class=\"flavor\"><i>It’s said a chaos shard is a portion of its master’s mind warped into a spirit form, but no master has ever confirmed this.</i></p><span class=\"clearIndent\"><b>Senses </b>blindsight 3</span><span class=\"clearIndent\"><b>Speed </b>fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana checks.<br/>* You gain a +2 bonus to saving throws against the dominated condition.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Chaos Echo: Once per encounter when you make an area or close attack, you can choose a creature within 5 squares of the chaos shard to also be a target for the attack.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 31.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('52','Gibbering Pet','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGibbering Pet\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=52\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Gibbering Pet</h1><p class=\"flavor\"><i>This lump of flesh has dozens of tiny eyes and mouths that constantly reshape and reform across its surface.</i></p><span class=\"clearIndent\"><b>Senses </b>all-around vision</span><span class=\"clearIndent\"><b>Speed </b>4, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* At the end of each extended rest, roll 1d4 to determine a language you can read and speak. Then roll 1d4 two more times to determine two skills to which you gain a +2 bonus (reroll if the same skill shows up twice). All benefits last until your next extended rest.<br/><table><tr><th>1d4</th><th>Language</th><th>Skill</th></tr><tr><td>1</td><td>Deep Speech</td><td>Bluff</td></tr><tr><td>2</td><td>Primordial</td><td>Diplomacy</td></tr><tr><td>3</td><td>Supernal</td><td>Intimidate</td></tr><tr><td>4</td><td>Abyssal</td><td>Streetwise</td></tr></table></span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gibbering Utterances: When a creature makes an attack that includes the gibbering pet, it takes a -2 penalty to all attack rolls on that attack.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 31.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('53','Shadow Incarnate','Familiar','Dragon Magazine 374','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nShadow Incarnate\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=53\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Shadow Incarnate</h1><p class=\"flavor\"><i>Your own shadow moves independently from you and can even separate to move on its own.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>8, fly 8 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth checks.<br/>* You gain darkvision.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Shadow Shroud: Any square the shadow occupies is lightly obscured.<br/>Shadowcast: Once per encounter as a minor action, yo ucan determine range, line of sight, and line of effect for your arcaneattacks from your shadow until the end of your current turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/374\" target=\"_new\">Dragon Magazine 374</a>, page(s) 31.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('54','Vadalis-bred Griffon','Companion','Eberron Player\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nVadalis-bred Griffon\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=54\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Vadalis-bred Griffon</h1><p class=\"flavortext\">In the final years of the Last War, House Vadalis began training griffon riders—soldiers mounted on domesticated griffons.</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 20, Constitution 18, Dexterity 16, Intelligence 2, Wisdom 12, Charisma 6<br/><b>Size: </b>Large<br/><b>Speed: </b>6 squares, fly 8<br/><b>Defenses: </b>AC 13 + level, Fortitude 14 + level, Reflex 12 + level, Will 12 + level<br/><b>Hit Points: </b>16 + 10 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>2d6<br/><b>Melee Basic Attack: </b>Claw; level + 3 vs. AC; 2d6 + Strength modifier damage.<br/><b>Mount: </b>The Vadalis-bred griffon is a mount.<br/><b>Cooperative Charge: </b>When the Vadalis-bred griffon’s rider charges, the griffon can make a claw attack as a free action in addition to its rider’s charge attack.<br/><b>Trained Skills: </b>Athletics, Endurance, Perception<br/><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786951000\" target=\"_new\">Eberron Player\'s Guide</a>, page(s) 79.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('55','Oso de la Fez, Former Show Bear','Familiar','Dragon Magazine 376','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nOso de la Fez, Former Show Bear\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=55\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Oso de la Fez, Former Show Bear</h1><p class=\"flavor\"><i>Balanced atop his ball, wearing his small, red fez, this bear seems the picture of innocence—until he bellows with fury and lashes at enemies who mock him with his razor-sharp claws.</i></p><span class=\"clearIndent\"><b>Speed </b>4, fly 4 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* Grrrrrr: You gain a +2 bonus on Intimidate checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">His Own Bear: Oso can speak any language you know, and can converse with other creatures. Unlike mere familiars, you can’t hear everything Oso hears and says unless Oso lets you, and you would never be able to tell Oso what to say.<br/>Verrrry Dangerous: Oso deals 1d4 damage per tier to anyone who attacks him and misses.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/376\" target=\"_new\">Dragon Magazine 376</a>, page(s) 75.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('56','Bantam Fastieth','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBantam Fastieth\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=56\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Bantam Fastieth</h1><p class=\"flavor\"><i>A bipedal lizard with powerful running legs, this creature is a smaller cousin of the Talenta mount.</i></p><span class=\"clearIndent\"><b>Speed </b>8</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks to jump.<br/>* You can make a running jump after moving only 1 square.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Fastieth Activity: When you use a move action for any reason, you can move the bantam fastieth its speed.<br/>Running Companion: When you start your turn adjacent to your familiar, you gain a +2 bonus to speed until the end of your turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 31.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('57','Bloodthorn Vine Barb','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBloodthorn Vine Barb\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=57\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Bloodthorn Vine Barb</h1><p class=\"flavor\"><i>Inhabitants of Xen’drik sometimes transform the island’s deadly vegetation into companions.</i></p><span class=\"clearIndent\"><b>Speed </b>5, (forest walk)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Heal checks.<br/>* You gain forest walk (you ignore difficult terrain if it’s the result of trees, underbrush, plants, or natural growth).</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Bloodthorn Drain: Once per encounter as a free action, when you deal damage to an enemy adjacent to your bloodthorn vine barb, you gain 3 temporary hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: 6 temporary hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 9 temporary hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('58','Clockwork Scorpion','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nClockwork Scorpion\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=58\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Clockwork Scorpion</h1><p class=\"flavor\"><i>With a metallic clattering, this construct scuttles along beside you, stinger raised to inject its stored venom.</i></p><span class=\"clearIndent\"><b>Senses </b>tremorsense 5</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to attack rolls to grab an enemy.<br/>* The clockwork scorpion contains a small reservoir that can store 1 dose of a poison or alchemical oil that you can apply to a weapon. You can refill it during a rest.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Scorpion’s Injection: Once per encounter, when you or an ally hits an enemy adjacent to the clockwork scorpion with a weapon attack, that attack benefits from the effect of the stored substance, which is consumed.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('59','Dragonmark Reflection','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDragonmark Reflection\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=59\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Dragonmark Reflection</h1><p class=\"flavor\"><i>This tattoolike familiar is in the image of its master’s Mark of Making, and it overlays the Mark of Making while passive.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 5 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can perform the Arcane Mark ritual as a standard action for no cost, and other creation rituals require half the normal time for you.<br/>* You can create alchemical items one level higher than your level.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Infuse Mark: When you use healing infusion on an ally adjacent to your dragonmark reflection, that ally regains 2 additional hit points (if you are using curative admixture) or gains 4 additional temporary hit points when the ally ends the AC bonus (if you are using resistive formula).<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: 3 additional hit points or 6 additional temporary hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 4 additional hit points or 8 additional temporary hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('60','Floating Weapon','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFloating Weapon\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=60\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Floating Weapon</h1><p class=\"flavor\"><i>An animate weapon hovers nearby to threaten your foes.</i></p><span class=\"clearIndent\"><b>Speed </b>0, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff and Intimidate checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Defensive Stance: The floating weapon has a +2 bonus to all defenses.<br/>Offensive Partner: When you hit an enemy adjacent to the floating weapon with a weapon attack, you can shift 1 square.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('61','Gadfly','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGadfly\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=61\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Gadfly</h1><p class=\"flavor\"><i>This wee buzzing creation alerts your guardian to an incoming threat.</i></p><span class=\"clearIndent\"><b>Speed </b>, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You gain a +2 bonus to skill checks for warding rituals.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gadfly’s Bite: Any summoned ally adjacent to the gadfly gains a +2 bonus to attack rolls on opportunity attacks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('62','Magpie','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMagpie\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=62\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Magpie</h1><p class=\"flavor\"><i>Natural thieves, these glossy black-and-white birds are the preferred companions of larcenous arcanists.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception and Thievery checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Thieving Magpie: A magpie can make Thievery checks to pick pockets or use sleight of hand. It gains a +2 bonus to such checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('63','Marmoset','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMarmoset\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=63\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Marmoset</h1><p class=\"flavor\"><i>Some artificers seek these clever creatures from Xen’drik forests for their nimbleness and manual dexterity.</i></p><span class=\"clearIndent\"><b>Speed </b>6, climb 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Acrobatics checks.<br/>* You gain a +2 bonus to skill checks for exploration rituals.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Monkey Business: A marmoset can make Thievery checks to open locks or disable traps, gaining a +2 bonus to such checks. It can manipulate thieves’ tools to gain an additional +2 bonus to such checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('64','Octopus','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nOctopus\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=64\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Octopus</h1><p class=\"flavor\"><i>A master of camouflage that can squeeze into tiny spaces, an octopus is an ideal spy and assistant, especially to reavers of the Lhazaar Principalities.</i></p><span class=\"clearIndent\"><b>Speed </b>2, swim 6 (aquatic)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Stealth checks.<br/>* You can go 6 minutes before you must make Endurance checks to hold your breath, and you gain a +5 bonus to such checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Octopus’s Flexibility: An octopus can squeeze through gaps as small as half an inch wide.<br/>Aquatic: While underwater, an octopus has a +5 bonus to Stealth checks.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 32.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('65','Saddle Squire','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSaddle Squire\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=65\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Saddle Squire</h1><p class=\"flavor\"><i>This tiny construct assistant resembles a jockey and carries a replica saddle on its back.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Nature checks.<br/>* While you are mounted, your mount gains a +1 bonus to its speed.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Saddle Assistance: As a minor action, you can mount or dismount a friendly mount adjacent to the saddle squire.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('66','Soarwood Wings','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSoarwood Wings\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=66\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Soarwood Wings</h1><p class=\"flavor\"><i>This animated set of wings clings to your back like a newly emerged butterfly.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks to jump.<br/>* You gain a +1 bonus to your fly speed, when you have one.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Soarwood Lift: Once per encounter as a move action, your soarwood wings fly an adjacent ally (or you, if you are adjacent) up to 6 squares, then lower the creature gently to the ground.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('67','Summoner Homunculus','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSummoner Homunculus\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=67\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Summoner Homunculus</h1><p class=\"flavor\"><i>This entity is a collection of sprockets and wire that can spur on summoned creatures.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Endurance checks.<br/>* You can perform the Unseen Servant ritual as a standard action for no cost, when you have no extant unseen servants. You do not need to master the ritual to use this benefit.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Infuse Summoned: Any creature you have summoned gains a +1 bonus to all defenses and to attack rolls while adjacent to the summoner homunculus.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('68','Tome Caddy','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nTome Caddy\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=68\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Tome Caddy</h1><p class=\"flavor\"><i>The bent form of this tiny construct assistant supports a lectern.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana and Dungeoneering checks.<br/>* You can wield a tome in place of another kind of arcane implement you can use.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Book Bearer: You can wield a tome as an implement without occupying either of your hands.<br/>Summons Focus: Any creature you have summoned gains a +2 bonus to damage rolls while adjacent to the tome caddy.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: +3 bonus.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: +4 bonus.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('69','Warforged Faceplate','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWarforged Faceplate\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=69\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Warforged Faceplate</h1><p class=\"flavor\"><i>This tiny construct resembles the armored face of a warforged, and it clings to you with multiple jointed appendages.</i></p><span class=\"clearIndent\"><b>Speed </b>4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks.<br/>* You gain a +2 bonus to saving throws against ongoing damage.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Warforged Watchfulness: While you are asleep, a warforged faceplate remains aware of its surroundings, allowing you to notice approaching enemies and other events as though you were awake.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('70','Ambush Vine Shoot','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nAmbush Vine Shoot\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=70\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Ambush Vine Shoot</h1><p class=\"flavor\"><i>Inhabitants of the Xen’drik jungles bond with this climbing plant to exploit that environment’s hazards.</i></p><span class=\"clearIndent\"><b>Senses </b>tremorsense 5</span><span class=\"clearIndent\"><b>Speed </b>6, burrow 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks.<br/>* When you climb or swim, add 1 to the distance you move on a successful Athletics check.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Vine’s Clasp: Enemies take a -2 penalty to saving throws to end the immobilized or restrained conditions while adjacent to the ambush vine shoot.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('71','Crawling Clot','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCrawling Clot\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=71\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Crawling Clot</h1><p class=\"flavor\"><i>Favored by the Blood of Vol, this strange companion is crafted from blood collected during the cult’s sacraments.</i></p><span class=\"clearIndent\"><b>Speed </b>4, climb 4 (spider climb)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Heal checks.<br/>* You gain resist 5 necrotic. If you already have resist necrotic, increase it by 2. Your resist necrotic increases by 2 while you are bloodied.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Clotting Factor: Once per day, if your crawling clot is adjacent to you or an ally when that character takes damage, you can allow your crawling clot to be destroyed so that character can spend a healing surge.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('72','Dimensional Pet','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDimensional Pet\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=72\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Dimensional Pet</h1><p class=\"flavor\"><i>You imbue a homunculus with an elemental spirit that grants a bit of control over the space around you.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can read and speak Primordial.<br/>* When you teleport, add 1 to the distance you teleport.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Dimensional Lock: Once per encounter as an immediate interrupt, you can negate an attempt to teleport made by a creature adjacent to the dimensional pet. That creature cannot teleport again until the start of its next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 33.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('73','Living Infusion','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLiving Infusion\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=73\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Living Infusion</h1><p class=\"flavor\"><i>The bulging body of this strange homunculus resembles a flask.</i></p><span class=\"clearIndent\"><b>Speed </b>4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Heal checks.<br/>* You can perform rituals of the restoration category in half the casting time.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Mobile Infusion: If the living infusion is adjacent to the target of your healing infusion, each ally adjacent to the target also regains 1d6 hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 2d6 hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('74','Minor Foulspawn','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMinor Foulspawn\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=74\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Minor Foulspawn</h1><p class=\"flavor\"><i>This horror of Xoriat resembles an inside-out cat with three eyes and two mouths.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Dungeoneering checks.<br/>* You gain a +2 bonus to saving throws against fear effects.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Foulspawn Babble: Once per encounter as a free action, you can deal 1d10 extra psychic damage to a target adjacent to your minor foulspawn that you hit with an arcane attack power.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 2d10 extra psychic damage.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('75','Smith’s Hammer','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSmith’s Hammer\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=75\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Smith’s Hammer</h1><p class=\"flavor\"><i>This fine crafting tool assists in performing impromptu repairs or upkeep.</i></p><span class=\"clearIndent\"><b>Speed </b>4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* If you are an artificer, you begin each day with an additional use of your Arcane Empowerment class feature.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Hammer’s Ring: Once per encounter as an immediate interrupt, when an enemy makes an attack against you or an ally adjacent to your smith’s hammer, you can grant a +2 bonus to a single defense of that character against the triggering attack. If the ally is a construct, the bonus increases to +3. Your smith’s hammer is destroyed.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('76','Tinker Feyling','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nTinker Feyling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=76\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Paragon Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Tinker Feyling</h1><p class=\"flavor\"><i>This diminutive sprite has a natural touch with magic items, making it a favorite of artificers</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff and Thievery checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Feyling’s Reservoir: You can use your Arcane Empowerment class feature as a minor action to empower a weapon adjacent to the feyling. This does not grant you any additional uses of Arcane Empowerment.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('77','Lingering Nightmare','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLingering Nightmare\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=77\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Lingering Nightmare</h1><p class=\"flavor\"><i>Willing servants of the Dreaming Dark sometimes retain a memory of the quori that previously possessed them. That nightmare residue floats like a small cloud nearby, shimmering with its master’s changing mood.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>6, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks.<br/>* You gain resist 5 psychic. If you already have resist psychic, increase the resistance by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Nightmare Voice: When you hit an enemy adjacent to the lingering nightmare with a fear attack, roll 1d4 to determine the additional effect, which lasts until the end of your next turn.<br/>&nbsp;&nbsp;&nbsp;&nbsp;1: The target is slowed.<br/>&nbsp;&nbsp;&nbsp;&nbsp;2: The target takes a -2 penalty to Will.<br/>&nbsp;&nbsp;&nbsp;&nbsp;3: The target grants combat advantage.<br/>&nbsp;&nbsp;&nbsp;&nbsp;4. The target takes a -2 penalty to attack rolls.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('78','Silver Lamp','Familiar','Dragon Magazine 377','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSilver Lamp\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=78\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Epic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Silver Lamp</h1><p class=\"flavor\"><i>This floating censerlike lantern contains a mystic reflection of the Silver Flame.</i></p><span class=\"clearIndent\"><b>Speed </b>, fly 8 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Religion and Arcana checks.<br/>* You gain a +2 bonus to saving throws against effects created by demons.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Silver Beams: As a minor action, you can cause the silver lamp to glow or to stop glowing. While glowing, it sheds bright light in its square and in a 10-square radius.<br/>Glory of the Silver Flame: Once per encounter as a free action, you can deal 2d8 extra radiant damage to a demon target within the silver lamp’s light that you hit with an arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/377\" target=\"_new\">Dragon Magazine 377</a>, page(s) 34.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('79','Muse Sprite','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMuse Sprite\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=79\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Muse Sprite</h1><p class=\"flavor\"><i>The muse sprite is a small glowing humanoid creature with gossamer wings, and it serves you as a councilor and a messenger.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>4, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Diplomacy checks.<br/>* The muse sprite radiates dim light in a 2-square radius.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Bearer of News: The muse sprite can deliver a short written message to someone up to 5 miles away for you.<br/>Insightful Councilor: Once per milestone, when you make an Arcana, Nature, or History check, you can roll two d20s and take the higher roll.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 61.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('80','White-Eyed Crow','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWhite-Eyed Crow\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=80\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>White-Eyed Crow</h1><p class=\"flavor\"><i>A white-eyed crow has stark white eyes, without pupils, but it is not blind. This small black bird sees more than any mortal creature.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You can’t be surprised.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Fate’s Agent: When you spend an action point to make an attack, you can reroll one missed attack roll against a target of that attack if that target is adjacent to the white-eyed crow.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 61.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('81','Gallant Hawk','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nGallant Hawk\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=81\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Gallant Hawk</h1><p class=\"flavor\"><i>Noble hawks with sable wings and a white breast in the form of a shield, gallant hawk spirits have a form of raptor beauty whether at the glove or in flight.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* While a creature marked by you is adjacent to you, it takes a -3 penalty to attack rolls on attacks that don’t include you as a target instead of the normal -2 from being marked.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gallant’s Call: Once per milestone, as a minor action, you can mark each creature adjacent to the gallant hawk (save ends).</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 61.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('82','Moon Wisp','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMoon Wisp\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=82\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Moon Wisp</h1><p class=\"flavor\"><i>These wisps of pale moonlight move to and fro through the night like a luminous tadpole in a wine-dark sea.</i></p><span class=\"clearIndent\"><b>Speed </b>, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Arcana and Nature checks.<br/>* The moon wisp radiates dim or bright light in a radius from 2 to 10 squares. You can change the details as a minor action, but it defaults to dim light in a 2-square radius.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Moon Bite: Once per milestone, as a minor action, the moon’s light reveals truth. Until the end of your next turn, attacks against any creature within 2 squares of the moon wisp do not take the –2 penalty from concealment.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 61.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('83','Specter','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSpecter\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=83\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Specter</h1><p class=\"flavor\"><i>You have touched the Shadowfell and lived to speak of it. You even brought back a souvenir.</i></p><span class=\"clearIndent\"><b>Senses </b>Darkvision</span><span class=\"clearIndent\"><b>Speed </b>, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +4 bonus to monster knowledge checks to identify undead creatures.<br/>* You gain resist 5 necrotic. If already have resist necrotic, increase the resistance by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Incorporeal: The specter has phasing and does not provoke opportunity attacks.<br/>Undead: The specter is immune to necrotic damage.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 29.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('84','Least Air Elemental','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLeast Air Elemental\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=84\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Least Air Elemental</h1><p class=\"flavor\"><i>A wisp of air from the Elemental Chaos does your bidding on the mortal plane.</i></p><span class=\"clearIndent\"><b>Speed </b>, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You take half normal damage from falling.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Elemental Servant: As a minor action, you cause the familiar to pick up, move, or manipulate an unattended object weighing 20 pounds or less and carry it up to 5 squares.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 29.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('85','Tiny Gelatinous Cube','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nTiny Gelatinous Cube\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=85\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Tiny Gelatinous Cube</h1><p class=\"flavor\"><i>This palm-sized ooze tickles you with its weak acid.</i></p><span class=\"clearIndent\"><b>Speed </b>2</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain resist 5 acid. If you already have resist acid, increase the resistance by 2.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Cleaning: The familiar consumes nonliving organic objects that it touches at a rate of 1 pound of material per round.<br/>Transparent: The familiar is invisible.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 29.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('86','Least Earth Elemental','Familiar','Dragon Magazine 382','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLeast Earth Elemental\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=86\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Least Earth Elemental</h1><p class=\"flavor\"><i>A small creature made of earth and stone lumbers along as you command.</i></p><span class=\"clearIndent\"><b>Speed </b>2, burrow 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* When an attack knocks you prone, you can make a saving throw to avoid falling prone.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">You and the least earth elemental can clearly see creatures and objects in the same square as the least earth elemental, even if they are invisible, obscured, or outside line of effect, but they and the least earth elemental must be in contact with the ground or the same substance, such as water or a web.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/382\" target=\"_new\">Dragon Magazine 382</a>, page(s) 29.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('87','Fiddling Grig','Familiar','Dragon Magazine 390, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFiddling Grig\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=87\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Fiddling Grig</h1><p class=\"flavor\"><i>This tiny sprite has the upper body of a humanoid, but the wings, antennae, and legs of a cricket. It wears a gaily colored vest and plays a miniature fiddle.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>4, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">You can master and perform bard rituals as if you had the Ritual Caster feat. The grig can act as a focus for any bard ritual that requires a musical instrument worth less than 100 gp.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Irresistible Dance: Once per round, when you hit an enemy with an arcane attack power and the enemy is adjacent to the fiddling grig, that enemy cannot make opportunity attacks until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/390\" target=\"_new\">Dragon Magazine 390</a>, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('89','Coure Attendant','Familiar','Dragon Magazine 390, Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCoure Attendant\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=89\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Coure Attendant</h1><p class=\"flavor\"><i>Sometimes the Court of the Stars honors promising spellcasters with coure familiars, which look like miniature eladrin and have gossamer wings sprouting from their shoulders.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>4, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\"><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You gain a +2 bonus to Arcana checks.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; Once per round, you can retrieve or store an item as a free action instead of a minor action.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Feylight Form: Once per encounter as a minor action, the coure attendant can assume the form of a twinkling ball of light. While in this form, it gains a +2 bonus to all defenses and sheds dim light within 2 squares. Enemies grant combat advantage to you while in this light. The coure attendant is immobilized in this form. You can spend a free action to return it to its normal form.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/390\" target=\"_new\">Dragon Magazine 390</a>, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 131.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('90','Horse','Companion','Dragon Magazine 392','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nHorse\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=90\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Horse</h1><p class=\"flavortext\">Ranging from ponies to warhorses, the horse beast companion is most effective when ridden.<br><b>Mount Companion:</b> A horse beast companion is both a mount and a beast companion. It uses the rules for mounted combat (D&D Rules Compendium pages 252-255) except for when it comes to actions. It uses the beast companion rules for actions (see “Commanding a Companion,” Martial Power pages 41-42). Furthermore, the horse beast companion has the following additional rules.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;While you are mounted atop your beast companion and you use a ranger beast power that grants either you or your companion a shift, your companion shifts and you move with it.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;While you are mounted atop your beast companion and you use a ranger beast power that allows both you and your beast companion to move or charge, only the companion gains the move or charge, but you move with it.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;If both you and your beast companion are adjacent to the same target, you are considered flanking for purposes of beast power effects. The target does not grant combat advantage unless you are actually flanking it, or it grants combat advantage for some other reason</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 14, Constitution 14, Dexterity 16, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Medium or Large (you choose at creation, but must be one size larger than you if you want to use the Mount ability).<br/><b>Speed: </b>10 squares<br/><b>Defenses: </b>AC 14 + level, Fortitude 13 + level, Reflex 12 + level, Will 12 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d8<br/><b>Melee Basic Attack: </b>Hooves; level + 4 vs. AC; 1d8 + Strength modifier damage.<br/><b>Trained Skill: </b>Endurance<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/392\" target=\"_new\">Dragon Magazine 392</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('91','Simian','Companion','Dragon Magazine 392','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSimian\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=91\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1   class=\"player\">Simian</h1><p class=\"flavortext\">Simian beast companions include both monkeys and apes. Your simian companion could be as small as a spider monkey or as large as an ape.<br/><b>Manipulate Items:</b> A simian companion, more than any other beast companion, is adept at manipulating objects. Once per round on your turn, as a free action you can command a simian companion to drink a potion, open or close, pick up an item, or retrieve or stow an item</p><p class=\"flavor indent\"><b>Ability Scores: </b>Strength 14, Constitution 14, Dexterity 16, Intelligence 6, Wisdom 12, Charisma 6<br/><b>Size: </b>Small or Medium (you choose at creation)<br/><b>Speed: </b>6 squares, climb 6<br/><b>Defenses: </b>AC 14 + level, Fortitude 12 + level, Reflex 13 + level, Will 12 + level<br/><b>Hit Points: </b>14 + 8 per level<br/><b>Attack Bonus: </b>Level + 4<br/><b>Damage: </b>1d6<br/><b>Melee Basic Attack: </b>Bite; level + 4 vs. AC; 1d6 + Strength modifier damage.<br/><b>Trained Skills: </b>Athletics, Acrobatics<p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/392\" target=\"_new\">Dragon Magazine 392</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('92','Shadow Raven','Familiar','Heroes of Shadow','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nShadow Raven\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=92\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must have the Ravenkin Paragon path<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Shadow Raven</h1><p class=\"flavor\"><i>A small shadow takes the form of an inky black raven with glowing red eyes.</i></p><span class=\"clearIndent\"><b>Senses </b>darkvision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You gain a +4 bonus to all defenses during a surprise round.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Under Raven’s Wing: Once per encounter as a minor action, you and your allies adjacent to the shadow raven gain partial concealment until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/280880000\" target=\"_new\">Heroes of Shadow</a>, page(s) 146.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('93','Critic Lizard','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nCritic Lizard\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=93\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Critic Lizard</h1><p class=\"flavor\"><i>These irritable, multicolored, spiny-backed lizards are renowned for their ability to perceive danger before it strikes.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, climb 3</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Insight checks and Perception checks.<br/>* You gain a +1 bonus to initiative checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Danger Sense: Being surprised doesn\'t cause you to grant combat advantage.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Augment 1: When you are surprised, you can spend 1 power point to take a move action or a minor action during the surprise round.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('94','Floating Polyp','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nFloating Polyp\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=94\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Floating Polyp</h1><p class=\"flavor\"><i>This creature resembles a translucent jellyfish floating lazily through the air</i></p><span class=\"clearIndent\"><b>Senses </b>blind, blindsight 5</span><span class=\"clearIndent\"><b>Speed </b>0, fly 6 (hover; altitude limit 3)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks made to jump.<br/>* You can make an Acrobatics check to reduce the damage you take from falling even if you are not trained in that skill.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Shared Buoyancy: When you jump, you are considered to have a running start.<br/>Sudden Combustion: If the floating polyp takes fire or lightning damage, it is destroyed and explodes in a close burst 1. Creatures in the burst take 5 fire damage and are pushed 1 square.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('95','Hurrum','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nHurrum\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=95\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Hurrum</h1><p class=\"flavor\"><i>This colorful, speckled beetle is highly sought after for its ability to generate a soothing melody by vibrating its tiny wings.</i></p><span class=\"clearIndent\"><b>Speed </b>4</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* Rather than sleep, you enter a meditative state known as trance. You need to spend 4 hours in this state to gain the same benefits others gain from taking a 6-hour extended rest. While in a trance, you are fully aware of your surroundings, and you notice approaching enemies and other events as normal.<br/>* At the end of a short rest, you regain 1 additional hit point each time you spend a healing surge.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Overheat: If the hurrum ends your turn exposed to direct sunlight, it is destroyed.<br/>Pacifying Drone: While within 5 squares of the hurrum, any enemy takes a -2 penalty to Insight checks and Perception checks.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Augment 1: Once per encounter as a free action during your turn, you can spend 1 power point to slow an enemy within 5 squares of the hurrum until the end of your next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('96','Jank','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nJank\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=96\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Jank</h1><p class=\"flavor\"><i>The soft, golden pelts of these ferretlike mammals fetch a high price, but their poisonous spurs make them dangerous to trap.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, burrow 3 (loose earth only), climb 3</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Nature checks.<br/>* You gain low-light vision.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Expert Forager: During an extended rest, the jank can locate a quantity of food and water that is equivalent to one survival day.<br/>Poisonous Spur: Once per encounter as a minor action, you can apply the jank’s poison to a weapon you are carrying. The next creature hit with that weapon before the end of the encounter takes 3 extra poison damage.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: 6 extra poison damage.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 9 extra poison damage.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('97','Kes\'trekel','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nKes\'trekel\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=97\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Kes\'trekel</h1><p class=\"flavor\"><i>These grotesque carrion birds have a strong affinity with death.</i></p><span class=\"clearIndent\"><b>Speed </b>2, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Intimidate checks and Perception checks.<br/>* You gain a +1 bonus to damage rolls against bloodied creatures.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: +2 bonus.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: +4 bonus.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Horrific Aversion: Once per encounter as a minor action, the  kes’trekel can push one creature adjacent to it 1 square.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Augment 1: As above, except that the kes\'trekel can push all creatures adjacent to it.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('98','Kivit','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nKivit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=98\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Kivit</h1><p class=\"flavor\"><i>This aloof, gray-furred feline secretes a venomous musk that makes it unappetizing to predators.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>7</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Perception checks.<br/>* You gain a +2 bonus to all defenses against opportunity attacks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Poisonous Deterrent: Once per encounter, when a creature hits you or the kivit with a melee attack, you can deal 5 poison damage to the attacker as an opportunity action. <br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: 10 poison damage.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 15 poison damage.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('99','Sitak','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSitak\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=99\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Sitak</h1><p class=\"flavor\"><i>This gregarious, brightly colored, parrotlike bird is as intelligent as it is attractive.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>2, fly 5</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Bluff checks and Diplomacy checks.<br/>* You can read and speak one additional language of your choice.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Telepathic Mimicry: A sitak can telepathically communicate with other creatures that share a language with you and that are within 10 squares of it. When it communicates, you hear everything it transmits and hear everything spoken to it. You can dictate its answers if you choose. You do not need line of effect or line of sight to the sitak to use this benefit.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('100','Wrab','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nWrab\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=100\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Wrab</h1><p class=\"flavor\"><i>These reclusive, black-scaled, winged serpents use their empathic sense to hunt for food and to read the mental states of nearby creatures.</i></p><span class=\"clearIndent\"><b>Senses </b>blindsight</span><span class=\"clearIndent\"><b>Speed </b>2, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Diplomacy checks and Insight checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Empathic Guidance: Once per round, when you make an Insight check and dislike the result, you can reroll the skill check. You must use the second roll.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Augment 1: As above, except that you can use either the second die roll or a result of 10. <br/>Vampiric Vitality: Once per encounter, if the wrab starts your turn adjacent to a bloodied creature, you gain 4 temporary hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 11: 8 temporary hit points.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Level 21: 12 temporary hit points.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('101','Z\'tal','Familiar','Dragon Magazine 397','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nZ\'tal\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=101\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Z\'tal</h1><p class=\"flavor\"><i>This lizard chirps nervously as it hops around, and its sharp, feathered scales gleam in the light.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>6, (10 when running)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">* You gain a +2 bonus to Athletics checks made to jump.<br/>* You gain a +2 bonus to speed when running or charging.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Piercing Scream: Once per encounter as a minor action, the z’tal can unleash a piercing scream in a close blast 3. Any creature in the blast is deafened and cannot make opportunity attacks until the start of its next turn.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/397\" target=\"_new\">Dragon Magazine 397</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('102','Sprite','Familiar','Heroes of the Feywild','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSprite\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=102\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Sprite</h1><p class=\"flavor\"><i>Sprites are almost as common in the Feywild as birds are in the natural world. You have befriended a minor sprite, who helps you with its innate sense of magic and its glowing wings.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>4, fly 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\"><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You gain a +2 bonus to Arcana checks.<br/><img src=\"images/bullet.gif\" alt=\"\"/>&nbsp;&nbsp;&nbsp;&nbsp; You can make an Arcana check as a minor action, rather than a standard action, to sense the presence of magic.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Light: As a minor action, you command the sprite to shed bright light in a 4-square radius. As a free action, you can command the sprite to douse the light.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/317310000\" target=\"_new\">Heroes of the Feywild</a>, page(s) 132.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('105','Daolanin','Familiar','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDaolanin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=105\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must be a Sha\'ir<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Daolanin</h1><p class=\"flavor\"><i>The crude and abrasive daolanin compensates for its unpleasant disposition through the command it displays over elemental earth.</i></p><span class=\"clearIndent\"><b>Speed </b>6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Earth’s Upheaval: The ground adjacent to you and the daolanin is difficult terrain for enemies that lack earth walk.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gift of Stone: When you use an arcane daily attack power, the daolanin pushes one enemy adjacent to it up to a number of squares equal to your Constitution modifier.<br/>Elemental Conduit: Once per encounter, you can use your familiar’s space as the origin for a close arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 107.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('106','Djinnling','Familiar','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDjinnling\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=106\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must be a Sha\'ir<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Djinnling</h1><p class=\"flavor\"><i>The djinnling’s changeable nature does little to interfere with its willingness and ability to aid your cause.</i></p><span class=\"clearIndent\"><b>Speed </b>6, fly 4 (hover)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Forceful Gusts: You can slide one ally adjacent to you or to the djinnling 1 square as a minor action.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gift of Wind: When you use an arcane daily attack power, you slide one creature adjacent to the djinnling up to 2 squares, and the djinnling shifts up to 2 squares.<br/>Elemental Conduit: Once per encounter, you can use your familiar’s space as the origin for a close arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 107.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('107','Efreetkin','Familiar','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nEfreetkin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=107\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must be a Sha\'ir<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Efreetkin</h1><p class=\"flavor\"><i>The fires glowing from within an efreetkin’s blackened flesh hint at the malice these servants hold for natural creatures.</i></p><span class=\"clearIndent\"><b>Speed </b>8</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Friend of Flames: You gain resist 5 fire.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gift of Flame (fire): When you use an arcane daily attack power, one enemy adjacent to the efreetkin takes fire damage equal to 3 + your Constitution modifier.<br/>Elemental Conduit: Once per encounter, you can use your familiar’s space as the origin for a close arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 108.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('108','Maridan','Familiar','Heroes of the Elemental Chaos','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMaridan\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=108\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must be a Sha\'ir<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Maridan</h1><p class=\"flavor\"><i>The playful maridan brings levity to the most trying situations, yet its jocularity never interferes with the performance of its duty.</i></p><span class=\"clearIndent\"><b>Speed </b>6, swim 6</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Aquatic: You and the maridan can breathe underwater. In aquatic combat, you gain a +2 bonus to attack rolls against nonaquatic creatures.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Gift of Water:When you use an arcane daily attack power, each enemy adjacent to the maridan is slowed, and you (if you are adjacent to the maridan) and each ally  adjacent to it gain partial concealment. These effects last until the end of your next turn.<br/>Elemental Conduit: Once per encounter, you can use your familiar’s space as the origin for a close arcane attack power.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/356170000\" target=\"_new\">Heroes of the Elemental Chaos</a>, page(s) 108.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('113','Spiderling of Lolth','Familiar','Council of Spiders, Dragon Magazine 413','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSpiderling of Lolth\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=113\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Requirement:</b>You must have the Widow of Arach-Tinilith theme<br><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Spiderling of Lolth</h1><p class=\"flavor\"><i>Twisted and evil, Lolth’s children delight in binding their prey to keep for later feasting.</i></p><span class=\"clearIndent\"><b>Speed </b>4, climb 4 (spider climb)</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">Venom Affinity: You gain a +2 bonus to saving throws against ongoing poison damage.<br/>Webs of Malice: Enemies take a -1 penalty to saving throws against your divine attack powers that immobilize or slow them.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Minor Action (At-Will): As a minor action, a spiderling can slide a creature that is taking ongoing poison damage and within 5 squares of the familiar one square.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Event.php?x=dnd/4new/event/dndencounters\" target=\"_new\">Council of Spiders</a>, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/413\" target=\"_new\">Dragon Magazine 413</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Companion` VALUES ('114','Brownie','Familiar','Dragon Magazine 420','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBrownie\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=companion&amp;id=114\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<b>Heroic Tier</b><h1   class=\"familiar\"><span class=\"level\">Familiar</span>Brownie</h1><p class=\"flavor\"><i>When the shy brownie appears, it resembles a miniature elf dressed in clothing cobbled together from household bits and bobs.</i></p><span class=\"clearIndent\"><b>Senses </b>low-light vision</span><span class=\"clearIndent\"><b>Speed </b>5</span><span class=\"flavor\"><b>Constant Benefits</b></span><span class=\"clearIndent\">You gain a +2 bonus to Stealth and Thievery checks.</span><span class=\"flavor\"><b>Active Benefits</b></span><span class=\"clearIndent\">Unseen Servant: You can use a minor action to turn the familiar invisible. While invisible, the familiar can carry objects that weigh up to 25 pounds, and it can move and manipulate objects at your command.</span><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/420\" target=\"_new\">Dragon Magazine 420</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
