INSERT INTO `Glossary` VALUES ('11','blindsight','Monsters','Sense','Monster Manual 2, Monster Manual, Divine Power, Monster Vault: Threats to the Nentir Vale, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nblindsight\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=11\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">blindsight</h1><p class=\"flavor\">A creature that has blindsight can see normally regardless of how obscured an area is and regardless of whether creatures or objects are invisible. Blindsight usually has a radius, beyond which the creature relies on its other senses.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786951017\" target=\"_new\">Monster Manual 2</a>, page(s) 216, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217207200\" target=\"_new\">Monster Manual</a>, page(s) 280, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786949823\" target=\"_new\">Divine Power</a>, page(s) 159, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/283640000\" target=\"_new\">Monster Vault: Threats to the Nentir Vale</a>, page(s) 122, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 168.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text