INSERT INTO `Glossary` VALUES ('105','eladrin','Monsters','Race','Dungeon Magazine 160','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\neladrin\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=105\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">eladrin</h1><p class=\"flavor\">Eladrin are graceful warriors and wizards at home in the eldritch twilight of the Feywild and the dark forests of the world. Although most eladrin reside in the Feywild, a few have interests or dealings in the natural world, and it’s not uncommon to find eladrin living among humans, elves, dwarfs, dragonborn, and halflings.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/160\" target=\"_new\">Dungeon Magazine 160</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('106','primordial','Monsters','Keyword','Dragon Magazine 370','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nprimordial\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=106\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">primordial</h1><p class=\"flavor\">Creatures of raw elemental power.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/drtoc/370\" target=\"_new\">Dragon Magazine 370</a>.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('107','Short Rest','Rules','Rules Other','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nShort Rest\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=107\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Short Rest</h1><p class=\"flavor\">A short rest allows a creature to regain the use of encounter powers and spend healing surges to regain hit points. Short rests follow these rules.<br/><br/>Duration: A short rest is about 5 minutes long.<br/><br/>No Limit per Day: A creature can take an unlimited number of short rests per day.<br/><br/>No Strenuous Activity: Resting means just that. The creature can stand guard, sit in place, ride on a wagon or other vehicle, or do other tasks that don’t require much exertion.<br/><br/>Regain the Use of Powers: After a short rest, a creature regains the use of expended encounter powers.<br/><br/>Spend Healing Surges: After a short rest, a creature can spend as many healing surges as desired (see “Hit Points and Healing,? page 255). A creature that runs out of healing surges must take an extended rest to regain them.<br/><br/>Using Powers during the Rest: If a creature uses an encounter power (such as a healing power) during a short rest, it needs another short rest to regain the use of that power.<br/><br/>Interruptions: If a creature’s short rest is interrupted, the creature needs to rest for another 5 minutes to get the benefits of a short rest.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 263, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 172.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('108','Extended Rest','Rules','Rules Other','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nExtended Rest\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=108\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Extended Rest</h1><p class=\"flavor\">Duration: An extended rest is at least 6 hours long.<br/><br/>Once per Day: After a creature finishes an extended rest, it has to wait 12 hours before beginning another one.<br/><br/>No Strenuous Activity: A creature normally sleeps during an extended rest but doesn’t have to. The creature can engage in light activity that doesn’t require much exertion.<br/><br/>Regain Hit Points and Healing Surges: At the end of an extended rest, a creature regains all lost hit points and all spent healing surges.<br/><br/>Regain the Use of Powers: At the end of an extended rest, a creature regains the use of expended encounter powers and daily powers.<br/><br/>Action Points: At the end of an extended rest, a creature loses any unspent action points, but starts fresh with 1 action point.<br/><br/>Interruptions: If anything interrupts a creature’s extended rest, such as an attack, add the time spent dealing with the interruption to the total time the creature needs to spend in the extended rest.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 263, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 172.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('109','Round','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nRound\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=109\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Round</h1><p class=\"flavor\">A round represents about 6 seconds in the game world. In a round, every combatant takes a turn. See also turn and once per round.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 266, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 315.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('110','Turn','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nTurn\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=110\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Turn</h1><p class=\"flavor\">On a creature’s turn, it takes actions: a standard action, a move action, a minor action, and any number of free actions, in any order it wishes. See also once per turn.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 266, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 317.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('111','Once Per Round','Rules','Rules Combat','Player\'s Handbook, Player\'s Handbook 3, Rules Compendium, Monster Vault: Threats to the Nentir Vale','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nOnce Per Round\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=111\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Once Per Round</h1><p class=\"flavor\">Some effects are usable only once per round. For example, if a creature uses an effect (such as a class feature) that is usable only once per round, it can’t use that effect again until the start of the creature’s next turn.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 269, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndlist&brand=All&year=All&type=CoreGameProducts\" target=\"_new\">Player\'s Handbook 3</a>, page(s) 222, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 314, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/283640000\" target=\"_new\">Monster Vault: Threats to the Nentir Vale</a>, page(s) 124.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('113','Limited Action','Rules','Rules Combat','Player\'s Handbook','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLimited Action\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=113\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Limited Action</h1><p class=\"flavor\">If you get to act in the surprise round, you can take a standard action, a move action, or a minor action. You can also take free actions, but you can’t spend action points. After every nonsurprised combatant has acted, the surprise round ends, and you can act normally in subsequent rounds.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 267.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('115','Standard Action','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nStandard Action\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=115\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Standard Action</h1><p class=\"flavor\">A standard action requires more effort than any other type and is usually the main action of a creature’s turn. Examples: Most attack powers, using one’s second wind, administering first aid to an ally.<br/><br/><table><tr><th>Action</th><th>Description</th></tr><tr><td>Administer a potion</td><td>Help an unconscious creature consume a potion</td></tr><tr><td>Aid another</td><td>Improve an ally\'s skill check or ability check</td></tr><tr><td>Aid attack</td><td>Improve an ally\'s attack roll</td></tr><tr><td>Aid defense</td><td>Improve an ally\'s defenses</td></tr><tr><td>Basic attack</td><td>Make a basic attack</td></tr><tr><td>Bull rush</td><td>Push a target 1 square and shift into the vacated space</td></tr><tr><td>Charge</td><td>Move and then make a melee basic attack or a bull rush</td></tr><tr><td>Coup de grace</td><td>Make a critical hit against a helpless enemy</td></tr><tr><td>Equip or stow a shield</td><td>Use a shield or put it away</td></tr><tr><td>Grab</td><td>Seize and hold an enemy</td></tr><tr><td>Ready an action</td><td>Ready an action to perform when a specified trigger occurs</td></tr><tr><td>Second wind</td><td>Spend a healing surge and gain a bonus to defenses (once per encounter)</td></tr><tr><td>Total defense</td><td>Gain a +2 to all defenses until next turn</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 267, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 194.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('116','Move Action','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMove Action\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=116\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Move Action</h1><p class=\"flavor\">Move actions involve movement from one place to another.<br/><br/><table><tr><th>Action</th><th>Description</th></tr><tr><td>Crawl</td><td>While prone, move up to half speed</td></tr><tr><td>Escape</td><td>Escape a grab and shift 1 square</td></tr><tr><td>Run</td><td>Move up to speed + 2; grant combat advantage until next turn and take a -5 penalty to attack rolls</td></tr><tr><td>Stand up</td><td>Stand up from prone</td></tr><tr><td>Shift</td><td>Move 1 square without provoking opportunity attacks</td></tr><tr><td>Squeeze</td><td>Reduce size by one category, move up to half speed, and grant combat advantage</td></tr><tr><td>Walk</td><td>Move up to walking speed</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 267, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 194.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documen