INSERT INTO `Glossary` VALUES ('117','Minor Action','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMinor Action\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=117\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Minor Action</h1><p class=\"flavor\">As the name implies, minor actions are simple and quick.<br/><br/><table><tr><th>Action</th><th>Description</th></tr><tr><td>Draw or sheathe a weapon</td><td>Use or put away a weapon</td></tr><tr><td>Drink a potion</td><td>Consume a potion</td></tr><tr><td>Drop prone</td><td>Drop down to lie on the ground</td></tr><tr><td>Load a crossbow</td><td>Load a bolt into a crossbow to fire it</td></tr><tr><td>Open or close a door</td><td>Open or close a door or container that isn\'t locked or stuck</td></tr><tr><td>Pick up an item</td><td>Pick up an object in one\'s space or in an unoccupied square within reach</td></tr><tr><td>Retrieve or stow item</td><td>Retrieve or stow an item on self</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 267, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 194.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n      