INSERT INTO `Glossary` VALUES ('167','Heavily Obscured','Rules','Rules Combat','Player\'s Handbook, Player\'s Handbook 3, Rules Compendium, Monster Vault: Threats to the Nentir Vale','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nHeavily Obscured\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=167\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Heavily Obscured</h1><p class=\"flavor\">A measure of visibility. A creature has total concealment when it is in a heavily obscured square, although it has only partial concealment against an enemy adjacent to it. Examples: Heavy fog, smoke, or foliage. Contrast with lightly obscured and totally obscured.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 281, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndlist&brand=All&year=All&type=CoreGameProducts\" target=\"_new\">Player\'s Handbook 3</a>, page(s) 220, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 312, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/283640000\" target=\"_new\">Monster Vault: Threats to the Nentir Vale</a>, page(s) 123.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (do