INSERT INTO `Glossary` VALUES ('315','Darkness','Rules','Rules Other','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nDarkness\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=315\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Darkness</h1><p class=\"flavor\">Darkness prevails outside on a moonless night and in lightless rooms.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 262, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 166.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('316','Sleeping and Waking up','Rules','Rules Other','Player\'s Handbook','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSleeping and Waking up\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=316\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Sleeping and Waking up</h1><p class=\"flavor\">You need at least 6 hours of sleep every day to keep functioning at your best. If, at the end of an extended rest, you haven’t slept at least 6 hours in the last 24, you gain no benefit from that extended rest.<br/><br/>When you’re asleep, you’re unconscious. You wake up if you take damage or if you make a successful Perception check (with a –5 penalty) to hear sounds of danger. An ally can wake you<br/>up by shaking you (a standard action) or by shouting (a free action).</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 263.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('317','Keeping Watch','Rules','Rules Other','Player\'s Handbook','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nKeeping Watch\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=317\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Keeping Watch</h1><p class=\"flavor\">Adventurers typically take turns keeping watch while their companions sleep. If five characters are in your group, each of you can take a turn on watch duty for 1½ hours and sleep for 6 hours, so that you spend a total of 7½ hours resting.<br/><br/>When it’s your turn on watch, you actively look for signs of danger. When you start your shift on watch, make a Perception check. If something occurs during your watch, the DM uses the result of your Perception check to determine whether you notice.<br/><br/>If your entire group sleeps at the same time without setting a watch, the DM uses your individual passive Perception scores, counting the –5 penalty for being asleep, to determine whether you hear approaching danger and wake up.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 263.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('318','Surprise Round','Rules','Rules Combat','Player\'s Handbook, Rules Compendium','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSurprise Round\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=318\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Surprise Round</h1><p class=\"flavor\">Some combat encounters begin with a surprise round, which occurs if any creatures are caught completely off guard at the start of battle. If even one creature is surprised, a surprise round occurs, and all creatures that aren’t surprised act in initiative order during that round. Surprised creatures can’t act at all during the surprise round.<br/><br/>Special Rules Two special rules apply to the surprise round.<br/><br/>Limited Action: If a creature is not surprised, it can take only one of the following actions on its turn during the surprise round: a standard action, a move action, or a minor action. The creature can also take free actions, but it cannot spend an action point. During the surprise round (but not on its turn), the creature can take an immediate action, as well as opportunity actions. See “Action Types? for definitions of these terms.<br/>&nbsp;&nbsp;&nbsp;&nbsp;After every creature that is not surprised has acted, the surprise round ends, and creatures can act normally in subsequent rounds.<br/><br/>Surprised: If a creature is surprised, it can’t take any actions, not even free actions, during the surprise round. The creature also grants combat advantage. As soon as the surprise round ends, the creature is no longer surprised.<br/>&nbsp;&nbsp;&nbsp;&nbsp;Surprised is one of many conditions that can be applied to a creature. See “Conditions?.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217367200\" target=\"_new\">Player\'s Handbook</a>, page(s) 267, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 191.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('320','Insubstantial','Rules','Rules Combat','Monster Manual 2, Monster Manual, Player\'s Handbook 3, Rules Compendium, Monster Vault: Threats to the Nentir Vale','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nInsubstantial\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=320\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Insubstantial</h1><p class=\"flavor\">When a creature is insubstantial, it takes half damage from any damage source, including ongoing damage. See also half damage.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786951017\" target=\"_new\">Monster Manual 2</a>, page(s) 218, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217207200\" target=\"_new\">Monster Manual</a>, page(s) 282, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndlist&brand=All&year=All&type=CoreGameProducts\" target=\"_new\">Player\'s Handbook 3</a>, page(s) 220, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 312, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/283640000\" target=\"_new\">Monster Vault: Threats to the Nentir Vale</a>, page(s) 124.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n    