INSERT INTO `Glossary` VALUES ('393','Monster Magic Treshold','Rules','Rules Table','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nMonster Magic Treshold\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=393\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">Monster Magic Treshold</h1><p class=\"flavor\">A monster equipped with magic items can use the powers those items grant. <br/><br/>Enhancement Bonuses: A monster benefits from an enhancement bonus to attack rolls, defenses, or  AC only if that bonus is higher than its magic threshold, as shown on the table below.<br/><br/>A monster’s magic threshold is an abstract representation of its equipment, power, and general effectiveness against characters of its level. If you give the monster a magic item that grants a bonus  to attack rolls and damage rolls or to defenses, subtract the magic threshold from that bonus before you apply it.<br/><br/>For example, if you give that 8th-level ogre savage a +2 magic greatclub, you add only a +1 bonus to its attack rolls and damage rolls, since its magic threshold is +1<br/><br/><table><tr><th>Monster Level</th><th>Magic Threshold</th></tr><tr><td>1st–5th</td><td>+0</td></tr><tr><td>6th–10th</td><td>+1</td></tr><tr><td>11th–15th</td><td>+2</td></tr><tr><td>16th–20th</td><td>+3</td></tr><tr><td>21st–25th</td><td>+4</td></tr><tr><td>26th–30th</td><td>+5</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 174.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('394','NPC Mannerism','Rules','Rules Table','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nNPC Mannerism\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=394\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">NPC Mannerism</h1><p class=\"flavor\">Describe a memorable characteristic of the NPC, something for the players to remember. They might forget a name, but they’ll remember the blacksmith with the elaborate vocabulary. Roll or pick a mannerism from the table, or make up your own.<br/><br/><table><tr><th>d20</th><th>Mannerism</th></tr><tr><td>1</td><td>Is prone to singing, whistling, or humming quietly</td></tr><tr><td>2</td><td>Speaks in rhymes or meter</td></tr><tr><td>3</td><td>Has particularly low or high voice</td></tr><tr><td>4</td><td>Slurs words, lisps, or stutters</td></tr><tr><td>5</td><td>Enunciates very clearly</td></tr><tr><td>6</td><td>Speaks loudly</td></tr><tr><td>7</td><td>Whispers</td></tr><tr><td>8</td><td>Uses flowery speech or long words</td></tr><tr><td>9</td><td>Frequently uses the wrong word</td></tr><tr><td>10</td><td>Uses colorful oaths and exclamations</td></tr><tr><td>11</td><td>Constantly makes jokes or puns</td></tr><tr><td>12</td><td>Is prone to predictions of certain doom</td></tr><tr><td>13</td><td>Fiddles and fidgets nervously</td></tr><tr><td>14</td><td>Squints</td></tr><tr><td>15</td><td>Stares into distance</td></tr><tr><td>16</td><td>Chews something</td></tr><tr><td>17</td><td>Paces</td></tr><tr><td>18</td><td>Taps fingers</td></tr><tr><td>19</td><td>Bites fingernails</td></tr><tr><td>20</td><td>Twirls hair or tugs beard</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 186.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('395','NPC Quirk','Rules','Rules Table','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nNPC Quirk\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=395\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">NPC Quirk</h1><p class=\"flavor\">In addition to covering the basic physical description of an NPC (height and build, color of skin, hair, and eyes, and so forth), think about a distinctive quirk to help set the character apart in the players’ minds. Roll or choose a quirk from the table, or come up with one of your own.<br/><br/><table><tr><th>d20</th><th>Quirk</th></tr><tr><td>1</td><td>Distinctive jewelry (earrings, necklace, bracelets)</td></tr><tr><td>2</td><td>Piercing</td></tr><tr><td>3</td><td>Flamboyant or outlandish clothes</td></tr><tr><td>4</td><td>Formal or very clean clothes</td></tr><tr><td>5</td><td>Ragged and very dirty clothes</td></tr><tr><td>6</td><td>Pronounced scar</td></tr><tr><td>7</td><td>Missing tooth</td></tr><tr><td>8</td><td>Missing finger</td></tr><tr><td>9</td><td>Unusual eye color (or two different colors)</td></tr><tr><td>10</td><td>Tattoo</td></tr><tr><td>11</td><td>Birthmark</td></tr><tr><td>12</td><td>Unusual skin color</td></tr><tr><td>13</td><td>Bald</td></tr><tr><td>14</td><td>Braided beard or hair</td></tr><tr><td>15</td><td>Unusual hair color</td></tr><tr><td>16</td><td>Nervous eye twitch</td></tr><tr><td>17</td><td>Distinctive nose</td></tr><tr><td>18</td><td>Distinctive posture—crooked or very rigid</td></tr><tr><td>19</td><td>Exceptionally beautiful</td></tr><tr><td>20</td><td>Exceptionally ugly</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 186.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('396','NPC Level Bonus and Magic Threshold','Rules','Rules Table','Dungeon Master\'s Guide','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nNPC Level Bonus and Magic Threshold\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=396\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">NPC Level Bonus and Magic Threshold</h1><p class=\"flavor\">As player characters gain levels, they choose feats and gain magic items that increase their attack bonuses and defenses. The level bonus, shown on the table below, is an abstraction that helps NPCs keep pace with characters. You can think of it as representing feats you’re not bothering to choose, low-level magic items, or the NPC’s intrinsic power.<br/><br/>Add this number to the NPC’s Armor Class and other defenses, attack rolls, and damage rolls. An NPC’s magic threshold is related to the level bonus. If you give an NPC a magic item that grants a bonus to attack rolls and damage rolls or to defenses, subtract the magic threshold from that bonus before you apply it.<br/><br/>For example, if you give a 12th-level NPC a +4 magic longsword, add only a +2 bonus to his or her attack rolls and damage rolls, since the magic threshold at that level is +2.<br/><br/><table><tr><th>Level</th><th>Level Bonus</th><th>Magic Threshold</th></tr><tr><td>1st–5th</td><td>+1</td><td>+0</td></tr><tr><td>6th–10th</td><td>+3</td><td>+1</td></tr><tr><td>11th–15th</td><td>+5</td><td>+2</td></tr><tr><td>16th–20th</td><td>+7</td><td>+3</td></tr><tr><td>21st–25th</td><td>+9</td><td>+4</td></tr><tr><td>26th–30th</td><td>+11</td><td>+5</td></tr></table></p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217507200\" target=\"_new\">Dungeon Master\'s Guide</a>, page(s) 187.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('399','altitude limit','Rules','Rules Other','Monster Manual 2, Player\'s Handbook 3','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\naltitude limit\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=399\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">altitude limit</h1><p class=\"flavor\">If a creature has a specified altitude limit, the creature crashes at the end of its turn if it is flying higher than that limit. See also fly speed.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786951017\" target=\"_new\">Monster Manual 2</a>, page(s) 216, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndlist&brand=All&year=All&type=CoreGameProducts\" target=\"_new\">Player\'s Handbook 3</a>, page(s) 220.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Glossary` VALUES ('401','earth walk','Rules','Rules Other','Monster Manual 2, Monster Manual, Rules Compendium, Monster Vault: Threats to the Nentir Vale','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nearth walk\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=glossary&amp;id=401\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"player\">earth walk</h1><p class=\"flavor\">A type of terrain walk. A creature that has earth walk ignores difficult terrain that is rubble, uneven stone, or an earthen construction.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/9780786951017\" target=\"_new\">Monster Manual 2</a>, page(s) 217, <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=products/dndacc/217207200\" target=\"_new\">Monster Manual</a>, page(s) 281, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/247530000\" target=\"_new\">Rules Compendium</a>, page(s) 311, <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/283640000\" target=\"_new\">Monster Vault: Threats to the Nentir Vale</a>, page(s) 123.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes