INSERT INTO `Terrain` VALUES ('118','Brazier','Terrain Power','Dungeon Magazine 177','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nBrazier\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=terrain&amp;id=118\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"dm\"><span class=\"level\">Single-Use Terrain</span>Brazier</h1><p class=\"flavor\"><i>This brazier burns brightly and hot. Tipping its iron stand sends blazing coals over nearby foes.</i></p><p class=\"powerstat\"><b>Standard Action</b></p><p class=\"powerstat\"><b>Requirement:</b> You must be adjacent to a brazier.</p><p class=\"flavor\"><b>Check:</b> Strength check (DC 20) to overturn the brazier.</p><p class=\"powerstat\"><b>Success:</b> The brazier is overturned, making an attack in a close blast 3.</p><p class=\"powerstat\"><b>Target:</b> Each creature in blast</p><p class=\"flavor\"><b>Attack:</b> +18 vs. Reflex</p><p class=\"powerstat\"><b>Hit:</b> Ongoing 10 fire damage (save ends).</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/default.asp?x=dnd/dutoc/177\" target=\"_new\">Dungeon Magazine 177</a>, page(s) 25.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n                var c = ar[i].attributes[\'class\'].value;\r\n                if (c == \'milevel\')\r\n                    ar[i].style.top = \"-24px\";\r\n                else if (c == \'miright\')\r\n                    ar[i].style.top = \"-18px\";\r\n            }\r\n        }\r\n    }\r\n</script>\r\n');
INSERT INTO `Terrain` VALUES ('119','Settled Ash','Terrain Power','Dark Sun Creature Catalog','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nSettled Ash\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=terrain&amp;id=119\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"dm\"><span class=\"level\">Single-Use Terrain</span>Settled Ash</h1><p class=\"flavor\"><i>Ash from plants destroyed by defiling magic coats the ground. The slightest disturbance kicks up an obscuring cloud.</i></p><p class=\"powerstat\"><b>Standard Action</b></p><p class=\"powerstat\"><b>Requirement:</b> You must be adjacent to a square of settled ash.</p><p class=\"flavor\"><b>Check:</b> Athletics check (easy DC) to raise a cloud of ash.</p><p class=\"powerstat\"><b>Success:</b> The ash fills the air, obscuring vision.</p><p class=\"powerstat\"><b>Target:</b> Each creature in a close blast 3</p><p class=\"flavor\"><b>Attack:</b> Level + 3 vs. Fortitude</p><p class=\"powerstat\"><b>Hit:</b> The target is blinded until the end of your next turn.</p><p class=\"powerstat\"><b>Effect:</b> The area of the blast is heavily obscured until the end of the encounter.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/253880000\" target=\"_new\">Dark Sun Creature Catalog</a>, page(s) 134.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n   