INSERT INTO `Terrain` VALUES ('120','Lightning Pillar Strike','Terrain Power','Dark Sun Creature Catalog','0','\r\n\r\n<!DOCTYPE HTML>\r\n<html>\r\n<head id=\"Head1\"><title>\r\nLightning Pillar Strike\r\n</title><meta http-equiv=\"X-UA-Compatible\" content=\"IE=EmulateIE7\" /><link href=\"styles/detail.css\" media=\"screen\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/print.css\" media=\"print\" rel=\"stylesheet\" type=\"text/css\" /><link href=\"styles/mobile.css\" media=\"handheld\" rel=\"stylesheet\" type=\"text/css\" />\r\n    \r\n    \r\n    \r\n\r\n</head>\r\n<body>\r\n    <form name=\"form1\" method=\"post\" action=\"display.php?page=terrain&amp;id=120\" id=\"form1\">\r\n<div>\r\n\r\n</div>\r\n\r\n\r\n    <div id=\"detail\">\r\n\r\n<h1 class=\"dm\"><span class=\"level\">At-Will Terrain</span>Lightning Pillar Strike</h1><p class=\"flavor\"><i>At your command, lightning lashes out from an ancient stone monolith.</i></p><p class=\"powerstat\"><b>Standard Action</b></p><p class=\"powerstat\"><b>Requirement:</b> You must be within 5 squares of a lightning pillar.</p><p class=\"flavor\"><b>Check:</b> Arcana, Nature, or Religion check (hard DC) to trigger the pillar’s attack.</p><p class=\"powerstat\"><b>Success:</b> You activate the lightning pillar.</p><p class=\"powerstat\"><b>Target:</b> One creature you choose in a close burst 5</p><p class=\"flavor\"><b>Attack:</b> Level + 3 vs. Reflex</p><p class=\"powerstat\"><b>Hit:</b> 2d6 + one-half level lightning damage.</p><p class=\"publishedIn\">Published in <a href=\"http://anonym.to/?http://www.wizards.com/dnd/Product.php?x=dnd/products/dndacc/253880000\" target=\"_new\">Dark Sun Creature Catalog</a>, page(s) 135.</p>\r\n    </div>\r\n    </form>\r\n</body>\r\n</html>\r\n\r\n<script type=\"text/javascript\">\r\n    if (document.all) {\r\n        if (document.documentMode != 9) {\r\n            var ar = document.getElementsByTagName(\'span\');\r\n            for (var i = 0; i < ar.length; i++ ) {\r\n